lb_manager_OnLeftClick()
{
	var strURL = "fk_company_id=" + _swdoc.oCust().fk_company_id;
	app.g.search_for("customers",false,strURL,function(oRes)
	{
		if(oRes.selectedkeys=="")return;

		var arrKeys = oRes.selectedkeys.split(",");
		var strMgrKey = arrKeys[0];
		if(strMgrKey==_swdoc.oCust().keysearch)
		{
			MessageBox('The manager selected is the current customer.');
			return false;
		}
		_swdoc.oCust().fk_manager = strMgrKey;
		_swdoc.UpdateFormFromData();

		return false;
	});
}

