lb_company1_OnDropDown()
{
	//-- DJH - 06.08.07
	//-- load company parent browser
	//-- Call new style company search form and resolve company if one is selected

	app.g.search_for("company",true,"",function(oRes)
	{
		if(oRes.selectedkeys=="")return;

		var arrKeys = oRes.selectedkeys.split(",");
		var arrName = oRes.selectedother.split(",");	
		strHash = arrKeys[0];

		if ((strHash == '') || (strHash == 'none')) return;

		if ((strHash == _swdoc.oCust().fk_company_id))return;

		var boolContinue = true;
		if( _swdoc.oCust().costcenter!="" || _swdoc.oCust().site!="")
		{
			boolContinue = confirm("Associating the customer to a different organisation will delete the customers charge centre and site associations. Do you want to proceed ?")
		}

		if(boolContinue)
		{
			_swdoc.oCust().fk_company_id = strHash;	
			_swdoc.oCust().site = "";
			_swdoc.oCust().costcenter = "";

			//-- clear customers sites
			if(_swdoc.oCust().fk_cmdb_id>0)
			{
				//-- updating so physically remove from db
				for (var x=0; x < sl_sites.rowCount();x++)
				{
					var sitecmdbid = sl_sites.GetItemTextRaw(x, 'fk_cmdb_id');
					if(sitecmdbid>0)
					{	
						app.cmdb.delete_meconfigrelation(sitecmdbid, _swdoc.oCust().fk_cmdb_id);
					}
				}
				_swdoc.refresh_customer_sitelist();
			}
			else
			{
				app.g.sqllist_clear(sl_sites);
			}

			//-- check if need to clear manager
			if(_swdoc.oCust().fk_manager!="")
			{
				var oRec = app.g.get_record("userdb", _swdoc.oCust().fk_manager);
				if(oRec)
				{
					if(oRec['pk_company_id']!=strHash)_swdoc.oCust().fk_manager = "";
				}
				else
				{
					_swdoc.oCust().fk_manager = "";
				}
			}		
			app._een(_swdoc.MF().other_items, true);
			_swdoc.UpdateFormFromData();	
		}
		return false;
	});
}

