mbtn_ci_actions1_OnMenuItem(strName, nItemID)
{
	var iAssignCI = 1;
	var iRemoveCI = 2;	
	var iRemoteSupport = 3;


	if(nItemID == iAssignCI)
	{
		var strURL = "fk_company_id=" + _swdoc.oCust().fk_company_id;
		app.cmdb.search_ci(true,strURL, function(sResults)
		{
			if (sResults.selectedkeys=="") return;

			//--
			//-- create ci relationship (always assume that customer always uses a CI)
			if(_swdoc.oCust().fk_cmdb_id>0)
			{
				var strRel = dd.GetGlobalParamAsString("Application Settings/CMDB/Types/DefaultCustCIRelationship");
				app.cmdb.generate_meconfigrelation(_swdoc.oCust().fk_cmdb_id, sResults.selectedkeys, strRel,"No");
			}   	
   			//-- Need to fetch each ci record so we can add relation properly
   			var oCI =  new Object();
			var arrCIS = sResults.selectedkeys.split(",");
			for(var y=0; y < arrCIS.length;y++)
			{
				var strKey = arrCIS[y];
				if(app.g.sl_findrow_byvalue(sl_configitems,strKey,"pk_auto_id")==-1)
				{
					var strParams = "ks="+app.pfu(_swdoc.oCust().keysearch)+"&key="+strKey;
					app.g.submitsqs("form/customer/create_config_relme", strParams);
				}
			}

			//-- ask if assigned cis are owned by the customer
			if(confirm("Is this customer the owner or sponsor of the selected configuration item(s)?"))
			{
				var strParams = "ks="+app.pfu(_swdoc.oCust().keysearch)+"&keys="+sResults.selectedkeys; 
				app.g.submitsqs("form/customer/set_customer_owner", strParams);
			}
			//-- refrsh this list
			mainform.sl_cust_services.Refresh();
			mainform.sl_configitems.Refresh();
			mainform.btn_search_OnPressed();
			//_swdoc.refresh_customer_cilist();		

			return;
		});
    }
	else if(nItemID == iRemoveCI)
	{
		//-- remove customer and ci relation
		var strCIids = app.g.sl_selectedkeys(sl_configitems,'pk_auto_id',true,",");
		if(strCIids=="")  return;

		if(!confirm("Are you sure you want to dis-associate the selected Configuration Items(s) from this customer record?"))return;

		//--
		//-- delete ci relationship (always assume that ME is parent)
		if(_swdoc.oCust().fk_cmdb_id>0)
		{
			app.cmdb.delete_meconfigrelation(_swdoc.oCust().fk_cmdb_id, strCIids);
		} 

		//-- 07.11.2008 - bug fix  73564
		var strPFSCustID = app.g.pfs(_swdoc.oCust().keysearch,true);
		if(app.cmdb.remove_ci_me_link(strCIids, strPFSCustID))
		{

		}	

		var strParams = "keys="+app.pfu(strCIids);
		app.g.submitsqs("form/customer/clear_ci_owner", strParams);
		mainform.sl_configitems.Refresh();
		mainform.btn_search_OnPressed();
		//_swdoc.refresh_customer_cilist();
	}
	else if(nItemID==iRemoteSupport)
	{
		//-- Remote Support Enabled?
		if(app.g.remote_support_enabled())
		{
			//-- Attempt to launch a pinned support session
			var intItem = app.g.sl_getrawcolvalue(sl_configitems,-1,"pk_auto_id");
			var oCI = app.g.get_record("config_itemi",intItem);
			app.g.start_pinned_remote_support(oCI);
		}	
		else
		{
			MessageBox("Remote Support is not available for this item");
		}
	}




}

