mbtn_role_actions_OnMenuItem(strName, nItemID)
{
	var iAssignCI = 1;
	var iRemoveCI = 2;	


	if(nItemID == iAssignCI)
	{
		app.g.popup("cfh_search_role", "",function(aForm)
		{
		var selectedKeys = aForm.document.returnedKeys;
		var selectedValues = aForm.document.returnedValues;
		if(selectedKeys=="")return;

		arrkeys = selectedKeys.split(",");
		arrvalues = selectedValues.split(",");

		var arrExisting = new Array;
   		var strParams = "rakey="+app.pfu(_swdoc.oCust().cfh_ra);
   		var oRS = app.g.get_sqrecordset("form/customer/select_cfh_role", strParams);
		while(oRS.Fetch())
		{
			var newValue 		= app.g.get_field(oRS,"role_id");	
			arrExisting[newValue] = newValue;			
		}

		for(var i = 0; i<arrkeys.length;i++){
			if(arrExisting[arrkeys[i]]==null)
			{
				var strParams = "rlid="+app.pfu(arrkeys[i])+"&rl="+app.pfu(arrvalues[i])+"&rid="+app.pfu(_swdoc.oCust().cfh_ra);
				app.g.submitsqs("form/customer/insert_cfh_role", strParams);
			}
		}

		mainform.sl_ra_roles.Refresh();
		return;
		});
    }
	else if(nItemID == iRemoveCI)
	{
		var strCIids = app.g.sl_selectedkeys(sl_ra_roles,'pk_auto_id',true,",");
		if(strCIids=="")  return;

		if(!confirm("Are you sure you want to remove the selected Roles from this customer record?"))return;

		app.g.submitsqs("delete.table","cs=1&table=cfh_ra_role&kv="+ strCIids);	
		mainform.sl_ra_roles.Refresh();
	}





}

