mb_org_action_OnMenuItem(strName, nItemID)
{

	if(!_swdoc.boolInserting){

		if(nItemID==1){
			//associate
			app.g.search_for("company",false,"",function(oRes)
			{
				if(oRes.selectedcmdbids=="")return;

				var arrCMDBKeys = oRes.selectedcmdbids.split(",");
				var l = arrCMDBKeys.length;
				for (var i = 0; i<l; i++)
				{
			  		var strParams = "ks="+app.pfu(_swdoc.oCust().keysearch)+"&key="+app.pfu(arrCMDBKeys[i]);
 					app.g.submitsqs("form/customer/add_multi_org_record_by_cikey", strParams);		 
				}
				_swdoc.MF().sl_orgs.Refresh();
				_swdoc.MF().sl_sites.Refresh();
				_swdoc.refresh_customer_orglist();
			});

		}
		else if(nItemID==2)
		{
			//dissassociate
	  		var strCompanyID = app.g.sl_getrawcolvalue(sl_orgs,sl_orgs.curSel,"pk_company_id");

   			if((strCompanyID==_swdoc.oCust().fk_company_id))
   			{
    			MessageBox("This organisation association cannot be deleted as it is the customers main organisation");
   				return;
   			}

			if(strCompanyID!="")
			{
				var strParams = "ks="+app.pfu(_swdoc.oCust().keysearch)+"&orgid="+app.pfu(strCompanyID);
				app.g.submitsqs("form/customer/remove_multi_org_record", strParams);

				var strParams = "orgid="+app.pfu(strCompanyID);
				var rsCompanySites = app.g.get_sqrecordset("form/customer/select_company_sites", strParams);
