	_swdoc.intCIPageNo = 1;
	var strFilter = ""

	if(fd_type.value != ""){
		_swdoc.strCIFilter += " AND ck_config_type LIKE '" + fd_type.value + "%'";
	}
	if(fd_id.value != ""){
		_swdoc.strCIFilter += " AND ck_config_item LIKE '% " + fd_id.text + " %'";
	}
	if(fd_description.value != ""){
		_swdoc.strCIFilter +=" AND description LIKE '% " + app.global.PrepareForSQL(fd_description.value) + " %'";
	}

	//--Update Document so Filter is Global
	strFilter = "&sf="+_swdoc.strCIFilter;


	var strCIlist = "";
	var currKey = 0;
	//-- get CI IDs customer is contact / sponsor of
	var p = {};
	p.mids = _swdoc.oCust().keysearch;
	var oRS = app.g.get_sqrecordset("cmdb/get_customers_cikeys.select",p);
	while(oRS.Fetch())
	{
		currKey = app.g.get_field(oRS,"PK_AUTO_ID");
		if (strCIlist!="")strCIlist +=",";
		strCIlist += currKey;
	}

	var currKey = 0;
	//-- get CI IDs used by customer
	var p = {};
	p.mids = userdb.fk_cmdb_id;
	var oRS = app.g.get_sqrecordset("cmdb/get_customer_uses_cikeys.select",p);
	while(oRS.Fetch())
	{
		currKey = app.g.get_field(oRS,"PK_AUTO_ID");
		if (strCIlist!="")strCIlist +=",";
		strCIlist += currKey;
	}

	//-- Add Paging
	strFilter += "&paged=true&start="+_swdoc.intCIPageNo+"&end=&pids="+strCIlist+"&webclient="+_swdoc.webclient+"";

	//-- apply filter
	app.g.sl_filter(sl_configitems,strFilter,null,0)
	_swdoc.MF().sl_configitems.Refresh();

	//Show Next Button
	if(sl_configitems.rowCount() < _swdoc.intPerPage)
	{
		app._een(_swdoc.MF().btn_next,false);	
