btn_unlock_instance1_OnPressed()
{
	// TODO: Add your event handler code here
	var strInstance = "";
	for (var x=0;x<_swdoc.MF().sl_wss_locks1.rowCount();x++)
	{
		if (_swdoc.MF().sl_wss_locks1.IsRowSelected(x))
		{
			strInstance = _swdoc.MF().sl_wss_locks1.GetItemTextRaw(x,0);
			break; // SQL List is deliberately not set to Multi Select
		}
	}
	if (strInstance == "")
	{
		MessageBox("Please select a SelfService Instance to unlock");
	}
	else // Unlock the customer access to the selected instance 
	{
		var xmlmc = new XmlMethodCall();
		xmlmc.SetValue("selfserviceInstance",strInstance);
		xmlmc.SetValue("customerId",_swdoc.oCust().keysearch);

		if(xmlmc.Invoke("session", "unlockCustomerAccount"))
		{
			var strXML = (app.bWebClient)? xmlmc._lastresult:xmlmc.GetReturnXml();
			var objRes = app.XMCResult(strXML);
			if(!objRes.success)
			{
				MessageBox("There was an error attempting to unlock this account against the selected SelfService Instance ("+strInstance+").");
			}	
		}	
		else
		{
			MessageBox("There was a system error attempting to unlock this account against the selected SelfService Instance ("+strInstance+").");
		}
	}

	// Need to refresh the list of locked Instances to remove the option to unlock this instance a second time (even though that shouldn't cause a problem)
	_swdoc.MF().sl_wss_locks1.Refresh();

}

