		xmlmc.SetValue("customerId",_swdoc.oCust().keysearch);
		xmlmc.SetValue("permanent","true");

		if(xmlmc.Invoke("session", "lockCustomerAccount"))
		{
			var strXML = (app.bWebClient)? xmlmc._lastresult:xmlmc.GetReturnXml();
