			MessageBox("There was a system error attempting to lock this account against the selected SelfService Instance ("+strInstance+").");
		}
	}

	// Need to refresh the list of locked Instances to add the newly locked instance
	_swdoc.MF().sl_wss_locks1.Refresh();

}

btn_reset_password1_OnPressed()
{
	// TODO: Add your event handler code here
	var strInstance = "";
	for (var x=0;x<_swdoc.MF().sl_wss_all1.rowCount();x++)
	{
		if (_swdoc.MF().sl_wss_all1.IsRowSelected(x))
		{
			strInstance = _swdoc.MF().sl_wss_all1.GetItemTextRaw(x,0);
			break; // SQL List is deliberately not set to Multi Select
		}
	}

	var boolCanSendPwdReset = true;
	var strMsg = "";
	if (strInstance == "")
	{
		strMsg = "Please select a SelfService Instance";
		boolCanSendPwdReset = false;
	}
	// check if there is an email in the database for the current customer. This needs to be in the database as the API uses the database value.
	var strParams = "ks="+app.pfu(_swdoc.oCust().keysearch);
	var aRS = app.g.get_sqrecordset("form/customer/check_cust_has_email", strParams);
	while(aRS.Fetch())
	{
		var strKey = app.g.get_field(aRS,"KS_CNT");
		if (strKey != "1") // there is NO email address against the customer record in the database
		{
			if (strMsg != "")
				strMsg += "\n";
			strMsg += "Please provide an email address for the customer. This must be saved to the database.";
			boolCanSendPwdReset = false;
			//break;
		}
	}

	if (boolCanSendPwdReset) // Lock the customer access to the selected instance 
	{
		var xmlmc = new XmlMethodCall();
		xmlmc.SetValue("selfServiceId",strInstance);
		xmlmc.SetValue("customerId",_swdoc.oCust().keysearch);

		if(xmlmc.Invoke("selfservice", "requestPasswordReset"))
		{
			var strXML = (app.bWebClient)? xmlmc._lastresult:xmlmc.GetReturnXml();
			var objRes = app.XMCResult(strXML);
			if(!objRes.success)
			{
				MessageBox("There was an error attempting to send a Password Reset for the selected SelfService Instance ("+strInstance+").");
			}
			else
			{
				MessageBox("A Password Reset email has been sent to the customer");
			}
		}	
		else
		{
			MessageBox("There was a system error attempting to send a Password Reset for the selected SelfService Instance ("+strInstance+").");
		}
	}
	else
	{
		MessageBox(strMsg);
	}
}

