function setup_auth_fields(strReqAuthValue)
{
	//-- if decision has already been made then disable fields
	var boolAuthDesicionMade = (_swdoc.OC().flg_authorised!=0);
	if(boolAuthDesicionMade)
	{
		app._een(_swdoc.MF().flg_requiresauth , false);
		app._een(_swdoc.MF().tb_authanalyst,false);
		app._een(_swdoc.MF().lbl_authanalyst,false);
	}
	else
	{
		//-- decision not made

		//--			
		//-- no auth requested - so allow it to be request at any time	
		if(strReqAuthValue=="0")
		{
			app._ema(_swdoc.MF().tb_authanalyst,false);
			app._een(_swdoc.MF().flg_requiresauth , true);

			//-- allow them to change who the authoriser is
			if(_swdoc.OC().authanalyst!="")
			{
				_swdoc.OC().authanalyst="";
				app._ete(_swdoc.MF().tb_authanalyst,"");
			}

			app._een(_swdoc.MF().tb_authanalyst , false);
			app._een(_swdoc.MF().lbl_authanalyst, false);
		}
		else
		{
			//-- auth has been requested - if auth analyst not already set then enable
			app._een(_swdoc.MF().tb_authanalyst,(_swdoc.OC().authanalyst==""));
			app._een(_swdoc.MF().lbl_authanalyst,(_swdoc.OC().authanalyst==""));
			app._ema(_swdoc.MF().tb_authanalyst,true);

			//-- give option to cancel auth request if they have the app right
			app._een(_swdoc.MF().flg_requiresauth ,  (app.itsm.can_modify_authrequest()));
		}		
	}

	//-- always allow authoriser to change decision
	app._een(_swdoc.MF().lb_analystdecision,(app.LC(_swdoc.OC().authanalyst)==app.LC(app.session.analystId)));
	app._een(_swdoc.MF().lbl_analystdecision,(app.LC(_swdoc.OC().authanalyst)==app.LC(app.session.analystId)));						

}

