btn_add_inc_OnPressed()
{


	//-- open select calls dialog so user can select 1 or more calls to add to the issue
	var strURL = "in_filter=callclass='Incident'";
	app.OpenForm("browse_calls", strURL, true,function(oForm)
	{
		if (oForm.document.strselectedpfskeys=="") return;

		var strCommaSepCallrefs = oForm.document.strselectedpfskeys;

		if(!confirm("Any selected incidents, that are already associated to another change request, will be re-associated to this change request.\n\nDo you want to continue?"))return;

		//-- call vpme script to add incidents
		var xmlmc = new XmlMethodCall;

		// Set up our input parameters
		xmlmc.SetValue("in_rfccallref", _swdoc.OC().callref);

		//-- get selected incidents
		var arrIncidents = strCommaSepCallrefs.split(",");
		for(var x=0;x<arrIncidents.length;x++)
		{
			xmlmc.SetValue("in_array_incidents", arrIncidents[x]);
		}
		// Invoke the method
		if(xmlmc.Invoke("VPME", "rfcAssociateIncidents"))
		{

			sl_incidents.Refresh();
			sl_incidents.SetRowSelected(0,true);	
		}
		else
		{
			MessageBox(xmlmc.GetLastError());
		}		

		_swdoc.refresh_impact_sqllists();
	});
}

btn_rem_inc_OnPressed()
{
	if(sl_incidents.rowCount()<1) return;

	//-- remove selected incidents
	if(!confirm("Are you sure you want to remove the association between the selected incident(s) and this change request?")) return;

	//-- call vpme to remove incident association
	var xmlmc = new XmlMethodCall;

	// Set up our input parameters
	xmlmc.SetValue("in_rfccallref", _swdoc.OC().callref);

	//-- get selected incidents
	var arrIncidents = app.g.sl_getselected_valuesdel(sl_incidents,"callref",",","", false).split(",");
	for(var x=0;x<arrIncidents.length;x++)
	{
		xmlmc.SetValue("in_array_incidents", arrIncidents[x]);
	}


	// Invoke the method
	if(xmlmc.Invoke("VPME", "rfcDisassociateIncidents"))
	{

		sl_incidents.Refresh();
		sl_incidents.SetRowSelected(0, true);
	}
	else
	{
		MessageBox(xmlmc.GetLastError());
	}	

	_swdoc.refresh_impact_sqllists();


}

