function setup_form_fields()
{
	_swdoc.strOriginalSLA = _swdoc.OC().priority;

	//-- get managers name
	app._ete(_swdoc.MF().lbl_manager, ": " + app.g.get_managername(_swdoc.oCust().fk_manager));

	_swdoc.setup_crating();
	
	//-- rfc ref
	_swdoc.format_linked_call_fields();
	
}

function format_linked_call_fields()
{
	//-- rfc ref
	if(_swdoc.OC().rfc_callref<1)
		app._ete(_swdoc.MF().tb_rfc,"");
	else
		app._ete(_swdoc.MF().tb_rfc, app.g.pad_callref(_swdoc.OC().rfc_callref));

	//-- prb ref
	if(_swdoc.OC().prb_callref<1)
		app._ete(_swdoc.MF().tb_problem, "");
	else
	 	app._ete(_swdoc.MF().tb_problem, app.g.pad_callref(_swdoc.OC().prb_callref));
}

function setup_crating()
{
	_swdoc.ib.StartAutoCycle(10000); //-- scroll every 10 seconds
	_swdoc.ib.RemoveItemsByGroup("rating");
	
	app._evi(_swdoc.MF().img_crating,true);
  	var webroot = "&[app.webroot]/";
  	if(_swdoc.OC().c_rating==0)
  	{
  		app._eim(_swdoc.MF().img_crating,webroot+ "clisupp/formimages/itsm/help.gif");
		app._ete(_swdoc.MF().lbl_crating, ": Request not yet rated");  		
		_swdoc.ib.AddItem("Customer Satisfaction : Not yet rated", "", "", "rating");		
  	}
 	
  	if(_swdoc.OC().c_rating==2)
  	{
  		app._eim(_swdoc.MF().img_crating,webroot+ "clisupp/formimages/itsm/cr_neu16.gif");
		app._ete(_swdoc.MF().lbl_crating, ": Request satisfaction Neutral");  		
		_swdoc.ib.AddItem("Customer Satisfaction : Neutral - " + _swdoc.OC().c_ratingtxt, "", "blue", "rating");				
  	}
	if(_swdoc.OC().c_rating==1)
	{
		app._eim(_swdoc.MF().img_crating,webroot+ "clisupp/formimages/itsm/cr_pos16.gif");
		app._ete(_swdoc.MF().lbl_crating, ": Request satisfaction Positive");  		
		_swdoc.ib.AddItem("Customer Satisfaction : Positive - " + _swdoc.OC().c_ratingtxt, "", "green", "rating");						
	}
  	if(_swdoc.OC().c_rating==3)
  	{
  		app._eim(_swdoc.MF().img_crating,webroot+ "clisupp/formimages/itsm/cr_neg16.gif");
		app._ete(_swdoc.MF().lbl_crating, ": Request satisfaction Negative");  		
		_swdoc.ib.AddItem("Customer Satisfaction : Negative - " + _swdoc.OC().c_ratingtxt, "", "red", "rating");								
  	}
	//-- show overall rating
	switch(_swdoc.oCust().flg_ocrating)
	{
		case 1:
			app._eim(_swdoc.MF().img_orating, webroot + "clisupp/formimages/itsm/cr_neg16.gif");
			app._ete(_swdoc.MF().lbl_orating,": Overall satisfaction negative");
			break;			
		case 2:
			app._eim(_swdoc.MF().img_orating, webroot + "clisupp/formimages/itsm/cr_neu16.gif");
			app._ete(_swdoc.MF().lbl_orating, ": Overall satisfaction neutral");
			break;						
		case 3:
		
			app._eim(_swdoc.MF().img_orating, webroot + "clisupp/formimages/itsm/cr_pos16.gif");
			app._ete(_swdoc.MF().lbl_orating, ": Overall satisfaction positive");
			break;						
		default:
			app._eim(_swdoc.MF().img_orating, webroot + "clisupp/formimages/itsm/cr_neu16.gif");
			app._ete(_swdoc.MF().lbl_orating, ": Overall satisfaction undetermined");
			break;
	}


}

//-- load full diary text into form fields - saves customer having to view call diary
function load_diary_textfields(boolLastOnly)
{
	var boolCache = (_swdoc.OC().status<15);
	app.g.get_call_first_updatetxt(_swdoc.OC().callref,boolCache,_swdoc.MF().tb_origtext);
}

//-- reset values after company id change
function reset_listbox_values()
{
	_swdoc.OC().fk_dept_code="";
	app._ete(_swdoc.MF().lb_oc_dept,""); // -- core product workaround - should reset when value is set to ""	
	_swdoc.OC().site="";
	_swdoc.OC().costcenter="";	
	_swdoc.UpdateFormFromData();
}

						OnRecordChanged(strTable)
{

    //-- When auto resolved user then we will need to refresh some of the sqllists as they dont do so
    //-- automatically
    if (IsObjectDefined("mainform") && strTable == "userdb")
    {
       	//-- set opencall fk_dept_code
		_swdoc.OC().fk_dept_code = userdb.fk_dept_code;
   
		//-- if customer does not belong to asset org then raise warning
		if(equipmnt.fk_company_id!="")
		{
			if (userdb.fk_company_id!=equipmnt.fk_company_id)
			{
				if(!confirm("The customer selected is not associated to the same organisation as the current asset. Do you want to proceed?"))
				{
					_swdoc.ChangeRecord("userdb", _swdoc.currentCustID );
					return;
				}	
			}
 		}
    
		//-- if the user is attached to an organisation, check organisation has a resolve message
		if(userdb.fk_company_id!="")
		{
			var comp = app.g.get_record("company",userdb.fk_company_id);
			if(comp.recordresolvedmsg!="")
			MessageBox(comp.recordresolvedmsg);
		}

		_swdoc.currentCustID = _swdoc.oCust().keysearch;
    	_swdoc.setup_form_fields();

		//-- check if customer is in active    	
    	var strCustStatusMsgValue = dd.GetGlobalParamAsString("Application Settings/CustomerStatusMessages/" + _swdoc.oCust().status);
		if(strCustStatusMsgValue!="")
		{
			MessageBox(strCustStatusMsgValue);
		}
    }    
        
	//-- 2.0.0 - contract check - but only if we are supporting orgs
	if(strTable=="equipmnt")
	{
		//-- if asset does not belong to customers org then raise warning
		if(userdb.fk_company_id!="")
		{
			if (userdb.fk_company_id!=equipmnt.fk_company_id)
			{
				if(!confirm("The asset selected is not associated to the same organisation as the current customer. Do you want to proceed?"))
				{
					_swdoc.ResetRecord("equipmnt");
				}	
			}
		}
	
		app.cfg.process_call_contract_check(equipmnt.equipid, true,_swdoc);
	}
	
}

