						OnRecordValueChanged(strTable, strColumn, strValue, strOldValue)
{
	var strBinding = strTable + "." + strColumn;
	switch(strBinding)
	{
		case "opencall.itsm_impact_level":
			var strSLA = app.itsm.get_sla_frommatrix(strValue, _swdoc.OC().itsm_urgency_level);
			if(strSLA!="") _swdoc.strMatrixSLA =  strSLA;
			
			break;
		case "opencall.itsm_urgency_level":		
			var strSLA = app.itsm.get_sla_frommatrix(_swdoc.OC().itsm_impact_level,strValue);
			if(strSLA!="") _swdoc.strMatrixSLA =  strSLA;
			break;
	}



}

						OnDataSaved()
{

	if(_swdoc.strOriginalSLA != _swdoc.strMatrixSLA)
	{
		app.itsm.handle_sla_matrix_change(_swdoc);
	}


}

						OnFormLoaded()
{
	//-- add _swdoc to global array so we can access it from other forms
	app.g.add_me_document("opencall",_swdoc.OC().callref,_swdoc);
		  
	_swdoc.setup_form_fields();

	//-- 12.07.2008 
	//-- disable or hide company field if internal support only
	app._een(_swdoc.MF().lb_company, ((dd.GetGlobalParamAsString("Application Settings/SupportExternalCompanies")==1)));
	if(!_swdoc.MF().lb_company.enable)
	{
		//-- check if we should hide
		app._evi(_swdoc.MF().lb_company,(dd.GetGlobalParamAsString("Application Settings/HideCompanyField")!=1));		
	}
	app._evi(_swdoc.MF().lbl_company, _swdoc.MF().lb_company.visible);	
	//-- eof hide / enable	    	
	
	_swdoc.load_diary_textfields();	
	_swdoc.currentCustID = _swdoc.oCust().keysearch;
	
}

						js_issue_OnLeftClick()
{
    //-- NWJ - 01.04.2003
    //-- check if we need to raise an issue
    //-- and alert managers
    if (arguments.length < 1) return;
    var problemref = arguments[0];
    
    //--
    //-- get afffected incidents and ci count for this problem
    //--
    ciresult= app.global.SqlExecute("swdata","select problem_callref from rel_prob2ci where is_caused_by=0 and problem_callref = " + problemref);
    incresult= app.global.SqlExecute("swdata","select problem_callref from rel_prob2inc where problem_callref = " + problemref);

    var totalcount = ciresult + incresult;
    //if (totalcount==0)totalcount++;

    //-- check if we have a HOT issue raised for this call
    var rec = new SqlRecord;
    var boolHOT = false;
    var boolKWN = false;
    if(rec.GetCacheRecordWhere("swissues", "mastercallref = " + problemref+ " and issuetype = 1"))
    {
        var issuenumber = rec.issueref;
        var affusers = rec.affectedusers;
        boolHOT=true;
    } 

    if(rec.GetCacheRecordWhere("swissues", "mastercallref = " + problemref + " and issuetype = 2"))
    {
        boolKWN=true;
        var kwnissuenumber = rec.issueref;
        var affusers = rec.affectedusers;
    } 
    if ((boolHOT) && (boolKWN))
    {
        //-- delete HOT issue as we have a known issue
        var hdcon = new HelpdeskSession;
        if(hdcon.Connect())
        {
            //-- Create an issue (i believe number is generated automatically)
            if(hdcon.BeginUpdateIssue(issuenumber))
            {   
                hdcon.SendNumber ("status",16);
                hdcon.Commit()
            }
            hdcon.Close()
        }       
        issuenumber = kwnissuenumber;
    }

    if ((boolHOT) || (boolKWN))
    {
        //-- reset effected by count
        if ((totalcount != affusers))
        {
            var hdcon = new HelpdeskSession;
            if(hdcon.Connect())
            {
                //-- Create an issue (i believe number is generated automatically)
                if(hdcon.BeginUpdateIssue(issuenumber))
                {   
                    hdcon.SendNumber ("affectedusers",totalcount);
                    hdcon.Commit()
                }
                hdcon.Close()
           }       
        }         
    }

}

						mbtn_profile_OnMenuItem(strName, nItemID)
{
	var iDesc = 2;
	var iProfile = 1;
	var iSumm=3;
	
	if(nItemID==iDesc)
	{
  		//if(_swdoc.updatedb.updatetxt == "")
		if(_swdoc.opencall.prob_text =="")  		
  		{
        	MessageBox("Please enter some descriptive text before trying to perform a Knowledge base search.");
        }
    	else
    	{
        	url = "hsl:KbSearch?search=" + _swdoc.opencall.prob_text;
        	app.global.OpenEmbeddedURL(url);
    	}
    }
	else if(nItemID==iProfile)
	{
  		if(_swdoc.OC().probcode == "")
  		{
        	MessageBox("Please select an incident profile before trying to perform a Knowledge base search.");
        }
    	else
    	{
        	url = "hsl:KbSearch?search=" + _swdoc.OC().probcode;
        	app.global.OpenEmbeddedURL(url);
    	}
    	return;
    }
	if(nItemID==iSumm)
	{
  		if(_swdoc.opencall.itsm_title == "")
  		{
        	MessageBox("Please enter a summary before trying to perform a Knowledge base search.");
        }
    	else
    	{   		
        	url = "hsl:KbSearch?search=" + _swdoc.opencall.itsm_title;
        	app.global.OpenEmbeddedURL(url);
    	}
    }

}

						mb_prb_action_OnMenuItem(strName, nItemID)
{
	var iNew = 1;
	var iFind = 2
	var iUnlink = 3;
	var iView = 4;


	if(nItemID==iNew)
	{
		app.global.RAISE_PRB_FROM_INCIDENT = _swdoc.OC().callref
		app.global.LogNewCall("Problem");

	}
	else if(nItemID==iFind)
	{
		//-- open select calls dialog so user can select 1 or more calls to add to the issue
		var strURL = "multiselect=0&in_filter=callclass = 'Problem'";
		app.OpenForm("browse_calls", strURL, true,function(oForm)
		{
			if (oForm.document.strselectedpfskeys=="") return;
	
			if(_swdoc.OC().prb_callref>0)
			{
				if(!confirm("Are you sure you want to replace the existing problem association?")) return;
			}
			
			//-- when call is saved vpme script will create diary entries
			var arrCommaSepCallrefs = oForm.document.strselectedpfskeys.split(",");	
			_swdoc.OC().prb_callref = arrCommaSepCallrefs[0];
			app._ete(_swdoc.MF().tb_problem , app.g.pad_callref(_swdoc.OC().prb_callref));
		});
	}
	if(nItemID==iUnlink)
	{
		//-- call vcm to unlink incident and problem
		if(_swdoc.OC().prb_callref>0)
		{
			if(!confirm("Are you sure you wish to remove this problem association?")) return;
			
			
			_swdoc.OC().prb_callref = "0";
			app._ete(_swdoc.MF().tb_problem , app.g.pad_callref(_swdoc.OC().prb_callref));

		}
		else
		{
			MessageBox("This incident is not currently associated to a problem.")
		}
		
	}
	if(nItemID==iView)
	{
		if(_swdoc.OC().prb_callref>0)
		{
			app.global.OpenCallDetailsView(_swdoc.OC().prb_callref);
		}
		else
		{
			MessageBox("This incident is not currently associated to a problem.")
		}
	}

}

						mb_rfc_action_OnMenuItem(strName, nItemID)
{
	var iNew = 1;
	var iFind = 2
	var iUnlink = 3;
	var iView = 4;


	if(nItemID==iNew)
	{
		app.global.RAISE_RFC_FROM_INCIDENT = _swdoc.OC().callref
		app.global.LogNewCall("Change Request");

	}
	else if(nItemID==iFind)
	{
		//-- open select calls dialog so user can select 1 or more calls to add to the issue
		var strURL = "multiselect=0&in_filter=callclass = 'Change Request'";
		app.OpenForm("browse_calls", strURL, true,function(oForm)
		{
			if (oForm.document.strselectedpfskeys=="") return;
	
			if(_swdoc.OC().rfc_callref>0)
			{
				if(!confirm("Are you sure you want to replace the existing change request association?")) return;
			}
			
			//-- when call is saved vpme script will create diary entries
			var arrCommaSepCallrefs = oForm.document.strselectedpfskeys.split(",");	
			_swdoc.OC().rfc_callref = arrCommaSepCallrefs[0];
			_swdoc.format_linked_call_fields();
		});
	}
	if(nItemID==iUnlink)
	{
		//-- 
		if(_swdoc.OC().rfc_callref>0)
		{
			if(!confirm("Are you sure you want to remove this change request association?")) return;
					
			//-- when call is saved vpme script will create diary entries
			_swdoc.OC().rfc_callref = "0";
			_swdoc.format_linked_call_fields();
		}
		else
		{
			MessageBox("This incident is not currently associated to a change request.")
		}
		
	}
	if(nItemID==iView)
	{
		if(_swdoc.OC().rfc_callref>0)
		{
			app.global.OpenCallDetailsView(_swdoc.OC().rfc_callref);
		}
		else
		{
			MessageBox("This incident is not currently associated to a change request.")
		}
	}

}

