	{
		app._ero(mainform.cb_condition , true);
	}
	//-- 12.07.2008 
	//-- disable or hide company field if internal support only
	app._een(lb_company , ((dd.GetGlobalParamAsString("Application Settings/SupportExternalCompanies")==1)));
	if(!lb_company.enable)
	{
		//-- check if we should hide
		app._evi(lb_company ,(dd.GetGlobalParamAsString("Application Settings/HideCompanyField")!=1));		
	}
	app._evi(lbl_company , lb_company.visible);	
	//-- eof hide / enable

	//-- load full diary entry
	var boolCache = (_swdoc.OC().status<15);
	app.g.get_call_first_updatetxt(_swdoc.OC().callref,boolCache,_swdoc.MF().tb_prbdesc);


	//-- core product issue with sqllists so we have to refresh manually
	_swdoc.setup_form_fields();
