OnRecordResolved(strTable)
{
	//-- 2.0.0 - contract check - but only if we are supporting orgs
	if(strTable=="equipmnt")
	{
		//-- if asset does not belong to customers org then raise warning
		if(userdb.fk_company_id!="")
		{
			if (userdb.fk_company_id!=equipmnt.fk_company_id)
			{
				if(!confirm("The asset selected is not associated to the same organisation as the current customer. Do you want to proceed?"))
				{
					_swdoc.ResetRecord("equipmnt");
				}	
			}
		}


		app.cfg.process_call_contract_check(equipmnt.equipid, true,_swdoc);

		//-- see if it needs change authorisation
		if(_swdoc.oAsset().flg_changeauth=="1")
		{
			MessageBox("The selected asset requires change authorisation. Please select an appropriate change manager for authorisation.");
			_swdoc.OC().flg_requiresauth = "1";
			_swdoc.UpdateFormFromData()
			_swdoc.setup_authorisation_fields(1);
		}
	}

    if (strTable == "userdb")
    {
		//-- if the user is attached to an organisation, check organisation has a resolve message
		if(userdb.fk_company_id!="")
		{
			var comp = app.g.get_record("company",userdb.fk_company_id);
			if(comp.recordresolvedmsg!="")
				MessageBox(comp.recordresolvedmsg);
		}
	}


    //-- When auto resolved user then we will need to refresh some of the sqllists as they dont do so
    //-- automatically
    if (IsObjectDefined("mainform") && strTable == "userdb")
    {

    	//-- set opencall fk_dept_code
		_swdoc.OC().fk_dept_code = userdb.fk_dept_code;

		//-- if customer does not belong to asset org then raise warning
		if(equipmnt.fk_company_id!="")
		{
			if (userdb.fk_company_id!=equipmnt.fk_company_id)
			{
				if(!confirm("The customer selected is not associated to the same organisation as the current asset. Do you want to proceed?"))
				{
					_swdoc.ResolveRecord("userdb", "keysearch" , _swdoc.currentCustID);
					return;
				}	
			}
		}

	   	_swdoc.currentCustID = userdb.keysearch;
    	//-- _swdoc.setup_customer_details()

		//-- check if customer is in active    	
    	var strCustStatusMsgValue = dd.GetGlobalParamAsString("Application Settings/CustomerStatusMessages/" + _swdoc.oCust().status);
		if(strCustStatusMsgValue!="")
		{
			MessageBox(strCustStatusMsgValue);
		}
		_swdoc.UpdateFormFromData();
    }    

}

OnRecordValueChanged(strTable, strColumn, strValue, strOldValue)
{
	var strBinding = strTable + "." + strColumn;

	//-- check if requires auth has changed
	switch(strBinding)
	{
	 	case "opencall.flg_requiresauth":
			_swdoc.setup_authorisation_fields(strValue);
			break;
		case "opencall.authanalyst":

			app._een(_swdoc.MF().lb_analystdecision,(app.LC(opencall.authanalyst)==app.LC(app.session.analystId)));
			app._een(_swdoc.MF().lbl_analystdecision,(app.LC(opencall.authanalyst)==app.LC(app.session.analystId)));			
			_swdoc.UpdateFormFromData();
			break;
		case "opencall.itsm_swtoday_on":
			app._ema(_swdoc.MF().date_start , (opencall.itsm_swtoday_on==1));
			app._ema(_swdoc.MF().date_end ,(opencall.itsm_swtoday_on==1));
			break;
		case "opencall.itsm_impact_level":
		case "opencall.itsm_urgency_level":		
			var strSLA = app.itsm.get_sla_frommatrix(_swdoc.OC().itsm_impact_level, _swdoc.OC().itsm_urgency_level);
			if(strSLA!="")
			{
				_swdoc.OC().priority = strSLA;
				_swdoc.UpdateFormFromData();
			}
			break;

	}



}

var strRaisedFromIssue = "";
var intRaisedFromInc = 0;
var intRaisedFromPrb = 0;
var currentCustID = "";

//-- pointers
function MF()
{
	return mainform;
}

function OC()
{
	return _swdoc.opencall;
}

function oCust()
{
	return _swdoc.userdb;
}

function oAsset()
{
	return _swdoc.equipmnt;
}

//--
//-- functions
//--
//-- setup forms fields based on authorisation requirement.
function setup_authorisation_fields(intRequiresAuthorisation)
{
	//-- enable disable fields
	var boolEnable = (intRequiresAuthorisation==1);

	//-- enable / disable fields
	app._een(_swdoc.MF().flg_requiresauth , ( (_swdoc.oAsset().flg_changeauth!="1") || (app.itsm.can_modify_authrequest()))); //-- is asset requires auth then disable ability to switch off auth


	app._een(_swdoc.MF().tb_authanalyst,boolEnable);
	app._een(_swdoc.MF().lbl_authanalyst,boolEnable);
	app._ema(_swdoc.MF().tb_authanalyst,boolEnable);
	if(!boolEnable)
	{
		//-- reset opencall values
		opencall.flg_authorised = "0";
		opencall.authanalyst = "";
		app._ete(_swdoc.MF().tb_authanalyst,"");
		_swdoc.UpdateFormFromData();
	}

}

//-- reset values after company id change
function reset_listbox_values()
{
	_swdoc.OC().fk_dept_code="";
	app._ete(_swdoc.MF().lb_oc_dept,""); // -- core product workaround - should reset when value is set to ""	
	_swdoc.OC().site="[Use Customer Site]";
	_swdoc.OC().costcenter="[Use Customer Charge Centre]";	
	_swdoc.UpdateFormFromData();
}

function ValidateForm()
{
	//-- check required fields
	if (!_swdoc.check_mandatory_fields("updatedb",mainform))return false;		
	if (!_swdoc.check_mandatory_fields("opencall",mainform))return false;

	return TRUE;
}

function check_mandatory_fields(strTable,oForm)
{
    for (var x = 0; x<oForm.elements.length;x++)
    {
        var element = oForm.elements[x];

        if ((element.mandatory) && (element.text ==""))
        {
            var colName = "";
            var fieldName = "(not specified)";
            if(element.dataRef!="")
            {
            	var arrInfo = element.dataRef.split(".");
            	var strTable = arrInfo[0];
            	var colName = arrInfo[1];
            	var fieldName = dd.tables[strTable].columns[colName.toLowerCase()].DisplayName;
            }
        	MessageBox("The field '" + fieldName + "' is a mandatory field and must be completed.");
            return false;
        }
    }
    return true;
}

OnCallLoggedOK(nCallRef, dateRespondBy, dateFixBy, strCustName, strSla)
{
	//-- rfc logged ok.
	//-- check if was raised from an issue - if so update issue
	if(_swdoc.strRaisedFromIssue!="")
	{
			//-- check if issue form is open - if so access it and set value and save
			var issueDoc = app.g.get_me_document("issue",_swdoc.strRaisedFromIssue);
			if(issueDoc!=false)
			{
				issueDoc.Issue().rfc_callref = app.g.callref_unpad(nCallRef);
				issueDoc.MF().btnSaveIssue_OnPressed();
			}
			else
			{
				//-- if not open update issue using hdcon
			}

	}

	if(_swdoc.intRaisedFromPrb>0)
	{
			//-- check if problem form is open - if so access it and set value and save
		var prbDoc = app.g.get_me_document("opencall",_swdoc.intRaisedFromPrb);
		if(prbDoc!=false)
		{
			prbDoc.OC().rfc_callref = app.g.callref_unpad(nCallRef);
			prbDoc.format_linked_call_fields();
			prbDoc.Save();
		}
		else
		{
			//-- if not open update iincident using hdcon
		}
	}

	if(_swdoc.intRaisedFromInc>0)
	{
			//-- check if issue form is open - if so access it and set value and save
		var incDoc = app.g.get_me_document("opencall",_swdoc.intRaisedFromInc);
		if(incDoc!=false)
		{
			incDoc.OC().rfc_callref = app.g.callref_unpad(nCallRef);
			incDoc.format_linked_call_fields();
			incDoc.Save();
		}
		else
		{
			//-- if not open update iincident using hdcon
		}
	}

}

