{
	return app.itsm.can_create(strCallclass,true);


	return true;
}

OnResolveRecord(strTable, strColumn, strValue)
{
	if((strTable=="userdb")||(strTable=="equipmnt"))
	{
		//-- store company id so can use in create cust or asset code
		app.global.logcall_addnew_orgid = _swdoc.oCust().fk_company_id;
		app.global.logcall_addnew_orgname = userdb.companyname;

	}

	if((strTable=="userdb"))
	{
		_swdoc.OC().fk_dept_code = "";
		if(IsObjectDefined("mainform"))
			app._ete(_swdoc.MF().lb_oc_dept,"");
		_swdoc.UpdateFormFromData();
	}
	return strValue;
}

OnValidate()
{
	//-- check that start date is less then end date
	if(opencall.itsm_schedstartx > opencall.itsm_schedendx)
	{
		MessageBox("The Scheduled from date is greater than the Sheduled to date. Please revise the dates before and try again.");
		return VALIDATE_FAIL;
	}
	if(!_swdoc.ValidateForm())return VALIDATE_FAIL;


	return VALIDATE_DEFAULT;
}

OnFormLoaded(bDataReloaded)
{

	//-- NWJ - 17.09.2007 - log call for user if being passed in (logging from customer form)
	if(app.global.LOG_CALL_FOR_USER!=null)
	{
		_swdoc.ResolveRecord("userdb", "keysearch", app.global.LOG_CALL_FOR_USER);
	}
	app.global.LOG_CALL_FOR_USER=null;	

	//-- raised from problem
	if(app.global.RAISE_RFC_FROM_PROBLEM!=null)
	{
		_swdoc.intRaisedFromPrb = app.global.RAISE_RFC_FROM_PROBLEM;
		var prbDoc = app.g.get_me_document("opencall",_swdoc.intRaisedFromPrb);
		if(prbDoc!=false)
		{
			//-- get values to copy across - summary, description etc
			_swdoc.OC().itsm_title = prbDoc.OC().itsm_title;
			_swdoc.OC().itsm_impact_level = prbDoc.OC().itsm_impact_level;
			_swdoc.OC().itsm_urgency_level = prbDoc.OC().itsm_urgency_level;
			_swdoc.OC().priority = prbDoc.OC().priority;
			_swdoc.OC().probcode = prbDoc.OC().probcode;
			_swdoc.OC().costcenter = prbDoc.OC().costcenter;
			_swdoc.OC().site = prbDoc.OC().site;
			_swdoc.updatedb.updatetxt = prbDoc.MF().tb_prbdesc.text;
			_swdoc.UpdateFormFromData();

			//-- resolve same related records
			_swdoc.ResolveRecord("userdb", "keysearch", prbDoc.OC().cust_id);
			_swdoc.ResolveRecord("equipmnt", "equipid", prbDoc.OC().equipment);

		}
	}
	app.global.RAISE_RFC_FROM_PROBLEM=null;		

	//-- raised from incident
	if(app.global.RAISE_RFC_FROM_INCIDENT!=null)
	{
		_swdoc.intRaisedFromInc = app.global.RAISE_RFC_FROM_INCIDENT;
		var incDoc = app.g.get_me_document("opencall",_swdoc.intRaisedFromInc);
		if(incDoc!=false)
		{
			//-- get values to copy across - summary, description etc
			_swdoc.OC().itsm_title = incDoc.OC().itsm_title;
			_swdoc.OC().itsm_impact_level = incDoc.OC().itsm_impact_level;
			_swdoc.OC().itsm_urgency_level = incDoc.OC().itsm_urgency_level;
			_swdoc.OC().priority = incDoc.OC().priority;
			_swdoc.OC().probcode = incDoc.OC().probcode;
			_swdoc.OC().costcenter = incDoc.OC().costcenter;
			_swdoc.OC().site = incDoc.OC().site;
			_swdoc.updatedb.updatetxt = incDoc.MF().tb_origtext.text;
			_swdoc.UpdateFormFromData();

			//-- resolve same related records
			_swdoc.ResolveRecord("userdb", "keysearch", incDoc.OC().cust_id);
			_swdoc.ResolveRecord("equipmnt", "equipid", incDoc.OC().equipment);
		}
	}
	app.global.RAISE_RFC_FROM_INCIDENT=null;	

	//-- 12.07.2008 
	//-- disable or hide company field if internal support only
	app._een(_swdoc.MF().lb_company , ((dd.GetGlobalParamAsString("Application Settings/SupportExternalCompanies")==1)));
	if(!_swdoc.MF().lb_company.enable)
	{
		//-- check if we should hide
		app._evi(_swdoc.MF().lb_company ,(dd.GetGlobalParamAsString("Application Settings/HideCompanyField")!=1));		
	}
	app._evi(_swdoc.MF().lbl_company , _swdoc.MF().lb_company.visible);	
	//-- eof hide / enable	    	
	if(_swdoc.oCust().keysearch !="")
	{
		_swdoc.OC().fk_dept_code = userdb.fk_dept_code;
	}
}
