			{
				_swdoc.OC().priority = strSLA;
				_swdoc.UpdateFormFromData();
			}
			break;
	}


}

OnResolveRecord(strTable, strColumn, strValue)
{
	if((strTable=="userdb")||(strTable=="equipmnt"))
	{
		//-- store company id so can use in create cust or asset code
		app.global.logcall_addnew_orgid = userdb.fk_company_id;
		app.global.logcall_addnew_orgname = userdb.companyname;

	}
	if(strTable=="userdb")
	{
		_swdoc.OC().fk_dept_code = "";
		if(IsObjectDefined("mainform"))
			app._ete(_swdoc.MF().lb_oc_dept,"");
		_swdoc.UpdateFormFromData();
	}
	return strValue;
}

