sl_asset_OnItemChecked(nRow, bChecked)
{
	//-- when row is checked load asset details
	if(_swdoc.intCurrentAssetRow>-1) sl_asset.SetRowChecked(_swdoc.intCurrentAssetRow, false);

	//-- only resovle and set if checked
	_swdoc.intCurrentAssetRow=-1;
	var strAssetID = sl_asset.GetItemTextRaw(nRow, "equipid");
	if(nRow>-1 && bChecked)
	{
		_swdoc.intCurrentAssetRow=nRow;	
		_swdoc.ResolveRecord("equipmnt", "equipid", strAssetID);
	}
	else
	{
		//-- reset if same asset as one that is resolved
		if(strAssetID==_swdoc.equipmnt.equipid)
		{
			_swdoc.ResetRecord("equipmnt");
		}
	}

}

flg_orgassets_OnValueChanged(strValue)
{
	var strFilter = "";
	//-- show customer or org assets 
	if(strValue=="1")
	{
		//-- show org assets that are not owned by anyone
		strFilter = "(owner='' or owner is null) and fk_company_id = '"+ app.global.PrepareForSQL(userdb.fk_company_id) + "'";		
		app._ete(cp_assets, "Organisational Assets (quick pick)");
	}
	else
	{
		//-- show customer assets
		strFilter = "(owner != '' or owner is null) and owner = '"+ app.global.PrepareForSQL(userdb.keysearch) + "'";
		app._ete(cp_assets,"Customer's Assets (quick pick)");
	}

	app._slf(sl_asset, strFilter);
	_swdoc.asset_check();

}
