OnSaveData()
{
	//-- store company name
	userdb.companyname = mainform.lb_company.display;
	//-- store fullname
	var strFirstName = userdb.firstname;
	if(strFirstName!="")strFirstName+= " ";
	userdb.fullname = strFirstName + userdb.surname;
	return true;
}

var boolInserting=false;
var array_sites = new Array();
function check_sites()
{

	if(mainform.IsObjectDefined("sl_sites"))
	{
		var strSite = "";

		for(var x=0;x<mainform.sl_sites.rowCount();x++)
		{
			strSite = mainform.sl_sites.GetItemTextRaw(x, "site_name");
			if(_swdoc.array_sites[strSite])
			{
					mainform.sl_sites.SetRowChecked(x,true);
			}
			else
			{
					mainform.sl_sites.SetRowChecked(x,false);
			}
		}	
	}

}

function store_sites()
{
	_swdoc.array_sites = new Array();
	var strSQL = "select FK_SITE_NAME from USERDB_SITE where FK_KEYSEARCH = '" + userdb.keysearch + "'";
	var oRS = app.g.get_recordset(strSQL,"swdata");
	while(oRS.Fetch())
	{
		var strSite = app.g.get_field(oRS,"FK_SITE_NAME");
		_swdoc.array_sites[strSite] = true;
	}	
}

OnDataSaved()
{
	//-- save user sites
	if(_swdoc.boolInserting)
	{
		for(strSite in _swdoc.array_sites)
		{
			if(_swdoc.array_sites[strSite])
			{
				var strSQL = "insert into USERDB_SITE (FK_SITE_NAME,FK_KEYSEARCH)values('" + app.g.pfs(strSite) + "','" +  app.g.pfs(userdb.keysearch) + "')";						
				app.g.submitsql(strSQL,false);
			}
		}
	}

}

mb_logcall_OnMenuItem(strName, nItemID)
{
	//-- assign user to log call for
	app.global.LOG_CALL_FOR_USER = userdb.keysearch;
