}

OnFormLoaded(bDataReloaded)
{
	if((userdb.keysearch=="")||(_swdoc.userdb.fk_company_id==""))
	{
		Tab1.ShowTabItem(2,false);
		_swdoc.boolInserting = true;
		_swdoc.userdb.fk_company_id = _swdoc.GetArg('fk_company_id');
		_swdoc.userdb.site = _swdoc.GetArg('site');
		_swdoc.UpdateFormFromData();
		_swdoc.ResetModiedFlag("userdb");


	}
	else
	{
		Tab1.ShowTabItem(2,true);
		_swdoc.boolInserting = false;
		_swdoc.store_sites();
	}
	app._etab(Tab1,0);


}

