							sl_contracts_OnItemSelected(nRow)
{
	//-- 14.12.2004
	//-- NWJ
	//-- Load Contract detail when record is selected

	var intContract_id  = sl_contracts.GetItemTextRaw(nRow,0);
    if (intContract_id == "")
	{
		if (sl_contracts.rowCount() > 0)
		{
			//-- user clicked just below last row
			//-- so force select of first row
			sl_contracts.SetRowSelected(0);
		}
		else
		{
			app._een(mbtn_company_contract_view,false);
		}
	}
	else
	{
		app._een(mbtn_company_contract_view,true);
	}

}

							tb_accman_OnLeftClick()
{
	//-- 09.03.2005
