							sl_sites_OnItemSelected(nRow)
{
	
	var site_id = app.g.sl_getrawcolvalue(sl_sites, sl_sites.curSel, "site_name");
	if (site_id != "")
	{
			_swdoc.site_key = site_id;
			app._een(mbtn_site_view,true);
			//app.g.eff(btn_del_site);
			//app.g.eff(btn_upd_site);	
	}
	else
	{
		if (sl_sites.rowCount() > 0)
		{
			app.g.sqllist_selectrow(sl_sites,"site_name",sl_sites_OnItemSelected);
		}
		else
		{
			//-- no records
			_swdoc.site_key = null;
			app._een(mbtn_site_view,false);
			//app.g.dff(btn_del_site);
			//app.g.dff(btn_upd_site);
		}
	}


}

							btn_site_OnPressed()
{
	//-- NWJ
	//-- launch www site
	
	strWebsite = _swdoc.oCompany().www_address;
	if ( (app.g.instr(strWebsite,"www.")) || (app.g.instr(strWebsite,"http://")) || (app.g.instr(strWebsite,"https://")) )
	{
		app.global.ShellExecute(strWebsite);
	}
	else
	{
		MessageBox("The WWW site address should begin with either http://, https:// or www.");
	}

}

