							sl_contact_OnItemSelected(nRow)
{
    //-- 26.07.2004
    //-- NWJ
    //-- Load contact details when selected.
    //var contact_id  = sl_contact.GetItemTextRaw(nRow,0);
    var contact_id = app.g.sl_getrawcolvalue(sl_contact, sl_contact.curSel, "keysearch");
    if (contact_id != "")
    {
     
        //-- show the update and delete button
        app._een(mbtn_company_contact_view,true);
    	//app.g.eff(btn_upd_contact);
    	//app.g.eff(btn_del_contact);
    }    
    else
    {
    
    	if (sl_contact.rowCount() > 0 )
    	{
    		sl_contact.SetRowSelected(0);
    		sl_contact_OnItemSelected(0);
    	}
    	else
    	{
    	
	        //-- 26.07.2004
        	//-- NWJ
        	//-- hide the update and delete button
        	app._een(mbtn_company_contact_view,false);
    		//app.g.dff(btn_upd_contact);
    		//app.g.dff(btn_del_contact);
        }
    }


    //app.g.eff(btn_new_contact);
    return;

}

