	//-- Add Site
	if(nItemID == iAddSite)
	{
		var strURL = "comp_name=" +  app.pfu(_swdoc.oCompany().companyname) + "&fk_company_id=" + app.pfu(_swdoc.oCompany().pk_company_id);
		app.OpenFormForAdd("TfSite", "", strURL, true,function()
