    	var selectedRow     = sl_contact.curSel;
    	var strConfirm		= "Are you sure you want to delete this customer from the database ?";
    	
		if(app.g.sl_deldbrow(sl_contact,selectedRow,"keysearch",null,strConfirm))
		{
			//-- relations deleted in vpme
			sl_contact.SetRowSelected(0,true);
		}
	}

}

