							mbtn_company_contract_actions_OnMenuItem(strName, nItemID)
{
    var iAddContract = 1;
	var iDeleteContract = 2;
	
	//-- Add Contract
	if(nItemID == iAddContract)
	{
		//-- 15.12.2004
		//-- NWJ
		//-- create a new contract for company
		var strURL = "fk_company_id=" + app.pfu(_swdoc.oCompany().pk_company_id);
  		app.g.sl_openformforadd(sl_contracts,"TfContract", strURL, 1,null,function()
  		{
  			_swdoc.MF().sl_contracts.Refresh();
			_swdoc.MF().sl_contracts.SetRowSelected(0);  
  		});  				
	}
	else if(nItemID == iDeleteContract)
	{
		//-- 15.12.2004
		//-- NWJ
		//-- delete contract
		var strContractID = app.g.sl_getrawcolvalue(sl_contracts,-1,"pk_contract_id");
		if (strContractID!="")
		{
			if (!confirm("Are you sure you want to delete the selected contract ?")) return;
			
			//-- delete the actual contract
			app.g.sqlexecute_delete("contract",strContractID);
			_swdoc.MF().sl_contracts.Refresh()
			_swdoc.MF().sl_contracts.SetRowSelected(0);
			
		}
	}
}

							lb_company_OnDropDown()
{

	//-- show list of companies
	var strURL = "";
	app.g.popup("browse_companies", strURL, function(oForm)
	{
		if (oForm.document.strselectedkeys=="") return;
		
		var strHash = oForm.document.strselectedkeys;
		
		if ((strHash == '') || (strHash == 'none')) return;
		
		if ((strHash == _swdoc.oCompany().pk_company_id))
		{
			MessageBox("A company cannot be associated as a parent of itself.");
			return true;
		}
		
		_swdoc.oCompany().fk_company_id = strHash;	
		_swdoc.UpdateFormFromData();	
		return false;
	});
}

