    //-- NWJ
    if ((_swdoc.oCompany().pk_company_id == "") || (_swdoc.oCompany().companyname == ""))
    {	
        _swdoc.boolInserting=true;
        
    }
    else if (_swdoc.oCompany().pk_company_id != '')
    {
  
    	_swdoc.boolInserting=false;  	
    }
	//-- init any special fields etc   
	_swdoc.setup_company_form(bDataReloaded);

}

							mb_cc_OnMenuItem(strName, nItemID)
{

	var iCreate = 1;

	var iDelete = 3;
	
	if(nItemID==iCreate)
	{
		var strURL = "companyname=" + app.pfu(_swdoc.oCompany().companyname) + "&fk_company_id=" + app.pfu(_swdoc.oCompany().pk_company_id);
		app.OpenFormForAdd("EfCharge Centre", "", strURL, true,function()
		{
			sl_cc.Refresh();
		});   	
	}
	else if(nItemID==iDelete)
	{
		if(sl_cc.curSel>-1)
		{
	   		var strConfirm	= "Are you sure you want to delete this charge centre from the database ?";
			if(!confirm(strConfirm))return;
		
  			var nCol = dd.tables[sl_cc.table].PrimaryKey;
  			
			var strQ = (dd.tables[sl_cc.table].columns[nCol].IsNumeric())?"":"'";  			
        	var strKeyValue = sl_cc.GetItemTextRaw(sl_cc.curSel, nCol);
        	if(strKeyValue != "")
        	{
            	var strSql = "DELETE FROM ";
            	strSql += sl_cc.table;
            	strSql += " WHERE ";
            	strSql += nCol;
            	strSql += " = " + strQ;
            	strSql += app.global.PrepareForSQL(strKeyValue);
            	strSql += strQ;
            	//MessageBox(strSql)
            	if(app.global.SqlExecute("swdata", strSql))
            	{
               		// This next call removes the row from the list, not the database
                	sl_cc.RemoveRow(sl_cc.curSel);
            	}
        	}
    	}
	}
}

