method.code:
						var boolInserting = false;

function oContract()
{
	return _swdoc.contract;
}

function MF()
{
	return _swdoc.mainform;
}

function init_form()
{
	//-- 27.10.2004
	//-- fix date fields as getrecord does not process them correctly
	if (_swdoc.oContract().pk_contract_id !="")
	{	
		_swdoc.boolInserting=false;
		_swdoc.MF().TabContract.ShowTabItem(1,true);		
		_swdoc.check_contract_state(true);
	}
	else
	{	
		//-- inserting
		_swdoc.boolInserting = true;
		_swdoc.oContract().fk_company_id= _swdoc.GetArg("fk_company_id");
		_swdoc.MF().TabContract.ShowTabItem(1,false);		
	}	
	
	_swdoc.ResetModiedFlag('contract');
}


//-- check contract state
function check_contract_state(boolSave)
{	

	if(boolSave==undefined)boolSave=true;
	//-- if the contract end date is less than todays date then set flag
	if ((app.g.fix_epoch(_swdoc.oContract().end_datex) < app.global.GetCurrentEpocTime()) ||
		(app.g.fix_epoch(_swdoc.oContract().start_datex) > app.global.GetCurrentEpocTime()) )	
	{
		//-- if system has not flagged it yet set flag and save
		if (_swdoc.oContract().flg_valid=="1")
		{	
			_swdoc.oContract().flg_valid="0";
			_swdoc.UpdateFormFromData();
			if(boolSave)_swdoc.Save(0);
		}
	}
	else
	{
		//-- if system has not flagged it yet set flag and save
		if (_swdoc.oContract().flg_valid==0)
		{	
			_swdoc.oContract().flg_valid="1";
			_swdoc.oContract().flg_ewemail_sent="0";			
			_swdoc.oContract().flg_expemail_sent="0";						
			_swdoc.UpdateFormFromData();
			if(boolSave)_swdoc.Save(0);
		}
	}
}

						OnDataSaved()
{

    var pDoc = app.g.get_me_document("COMPANY",_swdoc.oContract().fk_company_id);
    if(pDoc)
    {
    	var intCurSel = pDoc.MF().sl_contracts.curSel;
    	pDoc.MF().sl_contracts.Refresh();
    	pDoc.MF().sl_contracts.SetRowSelected(intCurSel,true);
    }

    //-- 
    //-- NWJ - show tab - and also update parent _swdoc
    if(_swdoc.boolInserting)
    {		
    	//-- create cmdb relationship
    	if(pDoc)
    	{
    		//-- need to reload site record to get fk_cmdb_id
    		_swdoc.LoadRecordData("swdata", "contract", _swdoc.oContract().pk_contract_id);
    		
    		var strRel = dd.GetGlobalParamAsString("Application Settings/CMDB/Types/DefaultOrgContractRelationship");
    		app.cmdb.generate_meconfigrelation(pDoc.oCompany().fk_cmdb_id,_swdoc.oContract().fk_cmdb_id,strRel);  
    	}
		_swdoc.boolInserting=false;
	}
    

	//-- ensure tab is active after saving
	_swdoc.init_form();

}

							OnFormLoaded()
{
	//-- 22.10.2004
	//-- NWJ
	//-- Get the passed in contract info

	//-- load fields
	_swdoc.init_form();

}

							mbtn_contract_actions_OnMenuItem(strName, nItemID)
{
	var iAssignCI = 1;
	var iRemoveCI = 2;	
	
	//-- get asset id
 	var strAssetID = sl_asset.GetItemTextRaw(sl_asset.curSel,"equipid");	
	
	if(nItemID == iAssignCI)
	{
		
		//-- browse for company assets and associate
		var strEquipidValues = app.g.sl_get_valuesdel(sl_asset,"equipid",",","'", true);
		if(strEquipidValues=="")strEquipidValues="''";
		var strURL = "in_filter=fk_company_id='" + app.pfu(app.g.pfs(_swdoc.oContract().fk_company_id)) + "' and equipid not in (" + strEquipidValues + ")";
		
		app.OpenForm("browse_assets", strURL, true,function(oForm)
		{
			if (oForm.document.strselectedpfskeys=="") return;
	
			//-- do any of the selected assets belong to another contract?
			if (oForm.document.strselectedother!="")
			{
				//-- prompt user
				if (!confirm("Some of the selected assets belong to other contracts. These assets will be updated to bind to this contract. Do you want to continue ?")) return;
			}
			
	
			var strUpdate = "update EQUIPMNT set FK_CONTRACT_ID = '" + app.g.pfs(_swdoc.oContract().pk_contract_id) + "' where EQUIPID in (" + oForm.document.strselectedpfskeys + ")";
			if(app.g.submitsql(strUpdate,true))
			{
				sl_asset.Refresh();
			}			
		});
    }
	else if(nItemID == iRemoveCI)
	{
		//-- remove link between ci and contract
		if (strAssetID!="")
    	{
    		if (!confirm("Are you sure you want to remove the selected asset from this contract ?")) return;
			var strUpdate = "update EQUIPMNT set FK_CONTRACT_ID = '' where EQUIPID = '" + app.g.pfs(strAssetID) + "'";
			if(app.g.submitsql(strUpdate,true))
			{
				sl_asset.Refresh();
			}

    	} 		
	}
		
}

							lb_ew_OnLeftClick()
{
	//-- 08.03.2005
	//-- NWJ
	//-- assign analyst to early warning
	var oA = app.g.pick_analyst("Send Early Warning To");
	if (oA)
	{
			_swdoc.oContract().fk_ewanalyst = oA.id ;
	}

	_swdoc.UpdateFormFromData();
	return false;
}

							lb_ew_OnRightClick()
{
	
	//-- clear lb
	_swdoc.oContract().fk_ewanalyst = "";
	app._ete(lb_ew,"");
	_swdoc.UpdateFormFromData();


	return true;
}

