function setup_form_fields(bDataReloaded)
{

	if ( (_swdoc.oCust().keysearch=="") || (_swdoc.oCust().firstname=="")	)
	{
		_swdoc.boolInserting=true;


		//-- clear distinct picklists as client bug where it remembers last value
		app._ete(_swdoc.MF().lb_manager,"");

		//-- set company and/or site
		app.g.popuplate_form_fromargs("userdb", _swdoc, true, true);		


		//-- are we creating from a log call auto resolve?
		if((_swdoc.oCust().fk_company_id=="") && (app.global.logcall_addnew_orgid!=null))
		{
			_swdoc.oCust().fk_company_id = app.global.logcall_addnew_orgid;
			_swdoc.oCust().companyname = app.global.logcall_addnew_orgname;
			app.global.logcall_addnew_orgid=null;
			app.global.logcall_addnew_orgname=null;

		}		

		if((_swdoc.oCust().fk_company_id=="")&&(dd.GetGlobalParamAsString("Application Settings/SupportExternalCompanies")!=1))
		{
			_swdoc.oCust().fk_company_id = dd.GetGlobalParamAsString("Application Settings/DefaultCompanyID");
		}		
	}
	else
	{

		_swdoc.boolInserting=false;

		//-- load customer sites
		_swdoc.strCurrentCo = _swdoc.oCust().fk_company_id;
		_swdoc.strCurrentSiteName =_swdoc.oCust().site;
		_swdoc.store_sites();
	}

	app._een(_swdoc.MF().mb_new_call,(!_swdoc.boolInserting));


	//-- 12.07.2008 
	//-- disable or hide company field if internal support only
	app._een(_swdoc.MF().lb_company1 , ((dd.GetGlobalParamAsString("Application Settings/SupportExternalCompanies")==1)));
	if(!_swdoc.MF().lb_company1.enable)
	{
		//-- check if we should hide
		app._evi(_swdoc.MF().lb_company1 ,(dd.GetGlobalParamAsString("Application Settings/HideCompanyField")!=1));		
	}
	app._evi(_swdoc.MF().lbl_company , _swdoc.MF().lb_company1.visible);	
	app._evi(_swdoc.MF().popup_company1 , _swdoc.MF().lb_company1.visible);	;
	//-- eof hide / enable


	if(bDataReloaded)
	{
		//-- do any data loadin when another customer record is loaded from db entity search view
		if(_swdoc.oCust().fk_manager=="")
		{
			app._ete(_swdoc.MF().lb_manager,"");
		}
	}

	_swdoc.MF().Tab1.ShowTabItem(2,!_swdoc.boolInserting);
	_swdoc.MF().Tab1.ShowTabItem(3,!_swdoc.boolInserting);	
	if(_swdoc.boolInserting)app._etab(_swdoc.MF().Tab1,0);

	_swdoc.UpdateFormFromData();	
	_swdoc.ResetModiedFlag("userdb");
}


function check_sites()
{
	if(mainform.IsObjectDefined("sl_sites"))
	{
		var strSite = "";

		for(var x=0;x<mainform.sl_sites.rowCount();x++)
		{
			strSite = mainform.sl_sites.GetItemTextRaw(x, "site_name");
			if(_swdoc.array_sites[strSite])
			{
					mainform.sl_sites.SetRowChecked(x,true);
			}
			else
			{
					mainform.sl_sites.SetRowChecked(x,false);
			}
		}	
	}
}

function store_sites()
{
	_swdoc.array_sites = new Array();
	var strSQL = "select FK_SITE_NAME from USERDB_SITE where FK_KEYSEARCH = '" + app.g.pfs(userdb.keysearch) + "'";
	var oRS = app.g.get_recordset(strSQL,"swdata");
	while(oRS.Fetch())
	{
		var strSite = app.g.get_field(oRS,"FK_SITE_NAME");
		_swdoc.array_sites[strSite] = true;
	}	
}

OnDataSaved()
{

    //-- 
    //-- NWJ - show tab - and also update parent _swdoc
    if(_swdoc.boolInserting)
    {		
    	_swdoc.boolInserting=false;		
		//-- save user sites
		for(strSite in _swdoc.array_sites)
		{
			if(_swdoc.array_sites[strSite])
			{
				var strSQL = "insert into USERDB_SITE (FK_SITE_NAME,FK_KEYSEARCH)values('" + app.g.pfs(strSite) + "','" +  app.g.pfs(userdb.keysearch) + "')";						
				app.g.submitsql(strSQL,false);
			}
		}
	}

	_swdoc.setup_form_fields()

}

