OnFormLoaded(bDataReloaded)
{

	//-- display user ratings	
	app._ete(lbl_incpos , userdb_rating.inc_pos);
	app._ete(lbl_incneu , userdb_rating.inc_neu);
	app._ete(lbl_incneg , userdb_rating.inc_neg);

	app._ete(lbl_rfcpos , userdb_rating.rfc_pos);
	app._ete(lbl_rfcneu , userdb_rating.rfc_neu);
	app._ete(lbl_rfcneg , userdb_rating.rfc_neg);		


	app._ete(lbl_1m_neg, userdb_rating.neg_m1);		
	app._ete(lbl_6m_neg, userdb_rating.neg_m6);				
	app._ete(lbl_12m_neg, userdb_rating.neg_m12);		

	app._ete(lbl_1m_neu, userdb_rating.neu_m1);		
	app._ete(lbl_6m_neu, userdb_rating.neu_m6);				
	app._ete(lbl_12m_neu, userdb_rating.neu_m12);				

	app._ete(lbl_1m_pos, userdb_rating.pos_m1);		
	app._ete(lbl_6m_pos, userdb_rating.pos_m6);				
	app._ete(lbl_12m_pos, userdb_rating.pos_m12);			

	//--
	//-- active and history totals
  	var webroot = "&[app.webroot]";

  	//-- ACTIVE TOTALS
	if((userdb_rating.neu_count >= userdb_rating.pos_count) && (userdb_rating.neu_count >= userdb_rating.neg_count))
	{
		app._eim(img_typeavg , webroot+ "/clisupp/formimages/itsm/cr_neu32.gif");
	}
	else if((userdb_rating.pos_count > userdb_rating.neu_count) && (userdb_rating.pos_count > userdb_rating.neg_count))
	{
		app._eim(img_typeavg , webroot+ "/clisupp/formimages/itsm/cr_pos32.gif");
	}
	else  
	{
		app._eim(img_typeavg , webroot+ "/clisupp/formimages/itsm/cr_neg32.gif");
	}


	//-- calc totals
	var intHistPosTotal = userdb_rating.pos_m1 + userdb_rating.pos_m6 + userdb_rating.pos_m12;
	var intHistNegTotal = userdb_rating.neg_m1 + userdb_rating.neg_m6 + userdb_rating.neg_m12;
	var intHistNeuTotal = userdb_rating.neu_m1 + userdb_rating.neu_m6 + userdb_rating.neu_m12;


	//-- HISTORICAL TOTALS
	if((intHistNeuTotal >= intHistNegTotal) && (intHistNeuTotal >= intHistPosTotal))
	{
		app._eim(img_allavg , webroot+ "/clisupp/formimages/itsm/cr_neu32.gif");
	}
	else if((intHistPosTotal > intHistNeuTotal) && (intHistPosTotal > intHistNegTotal))
	{
		app._eim(img_allavg , webroot+ "/clisupp/formimages/itsm/cr_pos32.gif");
	}
	else  
	{
		app._eim(img_allavg , webroot+ "/clisupp/formimages/itsm/cr_neg32.gif");
	}


	//-- NWJ
	//-- handle any on form loaded events here i.e. if inserting may need to hide / disable objects
	_swdoc.setup_form_fields(bDataReloaded)

}

