mbtn_action_asset1_OnMenuItem(strName, nItemID)
{

	var iAssignAsset = 1;		
	var iDeleteAsset = 2;	
	var iCreateAsset = 3;
	var iRemoveAsset = 4;			


	if(nItemID == iAssignAsset)
	{	
		//-- browse for company assets and associate
		var strURL = "in_filter=fk_company_id='" + app.pfu(app.g.pfs(_swdoc.userdb.fk_company_id)) + "'";

		app.OpenForm("browse_assets", strURL, true,function(oForm)
		{
			if(oForm.document.strselectedpfskeys=="") return;
			else
			{
				var strUpdate = "update EQUIPMNT set OWNER = '" + app.g.pfs(_swdoc.userdb.keysearch) + "' where EQUIPID in (" + oForm.document.strselectedpfskeys + ")";
				if(app.g.submitsql(strUpdate,true))
				{
					//-- any handling
					_swdoc.MF().sl_asset1.Refresh();
				}
			}
		});
    }
	else if(nItemID == iRemoveAsset)
	{
		//-- remove link between asset and site
		var strAssetID = sl_asset1.GetItemTextRaw(sl_asset1.curSel, 'equipid');		
		if (strAssetID!="")
    	{
    		if (!confirm("Are you sure you want to disassociate the selected asset from this customer ?")) return;

    		//-- update asset where main site is this one		
			var strUpdate = "update EQUIPMNT set OWNER = '' where EQUIPID = '" + app.g.pfs(strAssetID) + "' and OWNER='" + app.g.pfs(_swdoc.userdb.keysearch) + "'";
			if(app.g.submitsql(strUpdate,true))
			{
				_swdoc.MF().sl_asset1.Refresh();
			}
    	} 		
	}
	else if(nItemID == iDeleteAsset)
	{
		//-- remove link between ci and company
		var strAssetID = sl_asset1.GetItemTextRaw(sl_asset1.curSel, 'equipid');
		if (strAssetID!="")
    	{   	
 			var selectedRow     = sl_asset1.curSel;
    		var strConfirm		= "Are you sure you want to delete this asset from the database ?";

			if(app.g.sl_deldbrow(sl_asset1,selectedRow,"equipid",null,strConfirm))
			{
				//-- relations deleted in vpme
			}	    	
    	}
	}
	else if(nItemID == iCreateAsset)
	{
	   	var strURL = "&owner=" + app.pfu(_swdoc.userdb.keysearch) + "&fk_company_id=" + app.pfu(_swdoc.userdb.fk_company_id) + "&companyname=" + app.pfu(_swdoc.userdb.companyname);
    	app.OpenFormForAdd("TfAsset" ,"", strURL, true,function()
    	{
    		sl_asset1.Refresh();
   		    sl_asset1.SetRowSelected(0,true);	
    	});
	}

   	sl_asset1.Refresh();
   	sl_asset1.SetRowSelected(0,true);	




}

