						var boolInserting = false;

//--
//-- pointer to records
function oSite()
{
	return _swdoc.site;
}

function MF()
{
	return mainform;
}

function load_site_customers()
{
	var strKeys = "";
	//F0079044 - prepare for sql
	var strSQL = "select FK_KEYSEARCH from USERDB_SITE where FK_SITE_NAME = '" + app.g.pfs(_swdoc.oSite().site_name) + "'";
	var oRS = app.g.get_recordset(strSQL,"swdata");
	while(oRS.Fetch())
	{
		if(strKeys!="")strKeys+=",";
		strKeys += "'" + app.g.pfs(app.g.get_field(oRS,"FK_KEYSEARCH")) + "'";
	}	
	if(strKeys=="")strKeys+="'none'";	
	
		
	var strFilter = "keysearch in (" + strKeys + ") or site = '" + app.g.pfs(_swdoc.oSite().site_name) + "'";		
	app._slf(_swdoc.MF().sl_contacts,strFilter);
	_swdoc.MF().sl_contacts.SetRowSelected(0);				

}

function load_site_assets()
{

	var strFilter = "site = '" + app.g.pfs(_swdoc.oSite().site_name) + "'";		
	app._slf(_swdoc.MF().sl_asset,strFilter);
	_swdoc.MF().sl_asset.SetRowSelected(0);				

}

						OnSaveData()
{
	//-- store address in on field - useful for reporting or print outs
	var strAddress = (site.address_1!="")?site.address_1 + "\n":"";
		strAddress += (site.address_2!="")?site.address_2 + "\n":"";	
		strAddress += (site.address_3!="")?site.address_3 + "\n":"";			
		strAddress += (site.town!="")?site.town + "\n":"";			
		strAddress += (site.county!="")?site.county + "\n":"";			
		site.address = strAddress;
	return true;
}

							sl_contacts_OnItemSelected(nRow)
{
 	//-- 26.07.2004
    //-- NWJ
    //-- Load contact details when selected.
    var contact_id  = sl_contacts.GetItemTextRaw(nRow,'keysearch');
    if (contact_id != "")
    {
     
        //-- show the update and delete button
        app._een(mbtn_contact_view,true);
    }    
    else
    {
    
    	if (sl_contacts.rowCount() > 0 )
    	{
    		sl_contacts.SetRowSelected(0);
    		sl_contacts_OnItemSelected(0);
    	}
    	else
    	{
    		app._een(mbtn_contact_view,false);
        }
    }

    
    return;


}

							OnFormLoaded()
{
	//-- NWJ
	//-- 21.04.2005
	if ((_swdoc.oSite().site_name == "")||(_swdoc.oSite().site_type==""))
	{
		app.g.popuplate_form_fromargs("site", _swdoc, true, true);
		TabSiteInfo.ShowTabItem(2,false);
		TabSiteInfo.ShowTabItem(3,false);		
		app._etab(TabSiteInfo , 0);
		_swdoc.boolInserting=true;
		
		if(_swdoc.site.fk_company_id=="")
		{
			_swdoc.site.fk_company_id = dd.GetGlobalParamAsString("Application Settings/DefaultCompanyID");
			_swdoc.ResetModiedFlag("site");
		}		
	}
	else
	{
		TabSiteInfo.ShowTabItem(2,true);
		TabSiteInfo.ShowTabItem(3,true);		
		_swdoc.boolInserting=false;
		_swdoc.load_site_customers();
		_swdoc.load_site_assets();
		
	}
}

							mbtn_contact_view_OnMenuItem(strName, nItemID)
{
	var iProperties = 1;
	
	if(nItemID == iProperties)
	{
		sl_contacts.EditRecord(sl_contacts.curSel);
	}


}

							mbtn_contact_actions_OnMenuItem(strName, nItemID)
{
	var iNewContact = 1;
	var iDeleteContact = 2;
	var iSearchAdd = 3;
	var iRemove = 4;
	
	if(nItemID == iRemove)
	{
		var strKey = sl_contacts.GetItemTextRaw(sl_contacts.curSel, 'keysearch');
		if(strKey=="")return;
		
    			
		if(confirm("Are you sure you want to disassociate the selected customer from this site?"))
		{
			//-- update userdb where site is this site and is selected customer
			var strUpdate = "update USERDB set SITE = '' where KEYSEARCH = '" + app.g.pfs(strKey) + "' and SITE ='" + app.g.pfs(_swdoc.oSite().site_name)+ "'";
			app.g.submitsql(strUpdate,true);
			
			//-- delete existing rel if any
			var strDelete = "delete from USERDB_SITE where FK_SITE_NAME = '" + app.g.pfs(_swdoc.oSite().site_name) + "' and FK_KEYSEARCH = '" + app.g.pfs(strKey) +"'";
			app.g.submitsql(strDelete,true);
			
		}
		
		_swdoc.load_site_customers();
	}	
	else if(nItemID == iNewContact)
	{
		//-- NWJ
    	//-- Open new contact form
    	//-- pass in pk_company_auto_id so that we can link the two
    	var strURL = "fk_company_id=" + app.pfu(_swdoc.oSite().fk_company_id) + "&site=" + app.pfu(_swdoc.oSite().site_name);
		app.g.sl_openformforadd(sl_contacts,"TfCustomer", strURL, 1,null,function()
		{
			_swdoc.load_site_customers();
		});		
	}
	else if(nItemID == iDeleteContact)
	{
		
		//-- 29.07.2004
    	//-- NWJ
    	//-- Delete the selected contact
    	var selectedRow     = sl_contacts.curSel;
    	var strConfirm		= "Are you sure you want to delete this customer from the database ?";
    
		app.g.sl_deldbrow(sl_contacts,selectedRow,'keysearch',null,strConfirm);
		_swdoc.load_site_customers();
	}
	else if (nItemID == iSearchAdd)
	{
	
		//-- show list of company customers, that are not currently linked to this site, that can link to this site
		var strKeysearchValues = app.g.sl_get_valuesdel(sl_contacts,"keysearch",",","'", true);
		if(strKeysearchValues=="")strKeysearchValues="''";
		var strURL = "in_filter=fk_company_id='" + app.pfu(app.g.pfs(_swdoc.oSite().fk_company_id)) + "' and keysearch not in (" + strKeysearchValues + ")";
		app.g.popup("browse_customers", strURL, function(oForm)
		{
			if (oForm.document.strselectedpfskeys=="") return;
		 	  			
   			//- ask user to confirm that site is the customers main site 
   			if(confirm("Is this site the main site for the selected customer(s)?"))
   			{
				//-- update  userdb
				var strSQL = "update USERDB set SITE = '" + app.g.pfs(_swdoc.oSite().site_name) + "' where KEYSEARCH in (" + oForm.document.strselectedpfskeys + ")";
				app.g.submitsql(strSQL,false);
			}	
			else
			{
				//-- delete existing rel if any
				var strDelete = "delete from USERDB_SITE where FK_SITE_NAME = '" + app.g.pfs(_swdoc.oSite().site_name) + "' and FK_KEYSEARCH in (" + oForm.document.strselectedpfskeys +")";
				app.g.submitsql(strDelete,true);
				
				//-- create new rels
				var strKeysearch, strinsert = "";
				var arrayKeys = oForm.document.strselectedpfskeys.split(",");
				for(var x=0; x< arrayKeys.length;x++)
				{
					strKeysearch = arrayKeys[x];
					strInsert = "insert into USERDB_SITE (FK_SITE_NAME,FK_KEYSEARCH) values ('" + app.g.pfs(_swdoc.oSite().site_name) + "'," + strKeysearch + ")";
					app.g.submitsql(strInsert,true);				
				}
			}	
			_swdoc.load_site_customers();
					
			return;
		});
	}
}

							mbtn_view_ci_OnMenuItem(strName, nItemID)
{
  	var iProperties = 1;
	var iVCMParents = 2;	
	var iVCMChildren = 5;		
	var iInventory = 3;
	var iChangeSchedule = 4;

	//-- get intCI
  	var strAssetID = app.g.sl_getrawcolvalue(sl_asset,-1,"equipid");
  	if(strAssetID!="")
  	{ 	
		if(nItemID == iProperties)
		{
			sl_asset.EditRecord(sl_asset.curSel);
			sl_asset.Refresh();
			return;
    	}
		else if(nItemID == iInventory)
		{
			app.g.view_inventory(strAssetID,strAssetID);
		}
	
	}
}

							mbtn_asset_actions_OnMenuItem(strName, nItemID)
{
	var iAssignCI = 1;
	var iRemoveCI = 2;	
	var iCreateAsset = 3;
	var iDeleteAsset = 4;
	
	//-- get asset id
 	var strAssetID = sl_asset.GetItemTextRaw(sl_asset.curSel,"equipid");	
	
	if(nItemID == iAssignCI)
	{	
		//-- browse for company assets and associate
		var strEquipidValues = app.g.sl_get_valuesdel(sl_asset,"equipid",",","'", true);
		if(strEquipidValues=="")strEquipidValues="''";
		var strURL = "in_filter=fk_company_id='" + app.pfu(app.g.pfs(_swdoc.oSite().fk_company_id)) + "' and equipid not in (" + strEquipidValues + ")";

		app.OpenForm("browse_assets", strURL, true,function(oForm)
		{
			if(oForm.document.strselectedpfskeys=="") return;
			else
			{
				var strUpdate = "update EQUIPMNT set SITE = '" + app.g.pfs(_swdoc.oSite().site_name) + "' where EQUIPID in (" + oForm.document.strselectedpfskeys + ")";
				if(app.g.submitsql(strUpdate,true))
				{
					// -- Updated site table
					_swdoc.MF().sl_asset.Refresh();
				}
			}
		});
    }
	else if(nItemID == iRemoveCI)
	{
		//-- remove link between asset and site
		if (strAssetID!="")
    	{
    		if (!confirm("Are you sure you want to disassociate the selected asset from this site ?")) return;
    
    		//-- update asset where main site is this one		
			var strUpdate = "update EQUIPMNT set SITE = '' where EQUIPID = '" + app.g.pfs(strAssetID) + "' and SITE='" + app.g.pfs(_swdoc.oSite().site_name) + "'";
			if(app.g.submitsql(strUpdate,true))
			{
				// -- Update site table
				_swdoc.MF().sl_asset.Refresh();
			}
    	} 		
	}

	if(nItemID == iDeleteAsset)
	{
		//-- delete asset
		if (strAssetID!="")
    	{   	
 			var selectedRow     = sl_asset.curSel;
    		var strConfirm		= "Are you sure you want to delete this asset from the database ?";   		
			if(app.g.sl_deldbrow(sl_asset,selectedRow,"equipid",null,strConfirm))
			{
				//-- remove from asset_site table
			}	    	
    	}
	}
	else if(nItemID == iCreateAsset)
	{
	   	var strURL = "fk_company_id=" + app.pfu(_swdoc.oSite().fk_company_id) + "&companyname=" + app.pfu(_swdoc.oSite().comp_name) + "&site=" + app.pfu(_swdoc.oSite().site_name);
    	app.OpenFormForAdd("TfAsset" ,"", strURL, true,function()
    	{
    		//-- reload site list
			_swdoc.load_site_assets();
    	});
	}
	
	//-- reload site list
	_swdoc.load_site_assets();
			
}
