OnFormLoaded()
{
	var boolUpdate = (_swdoc.ACTION().pk_id!=0);
	if(boolUpdate)
	{
		//-- updating
		TabCond.ShowTabItem(2,true);				
		if(ci_avail_action.raise_itsm_sla>0)
		{
			_een(_swdoc.MF().lbl_r_sla, true);
			_een(_swdoc.MF().fld_r_sla, true);
			_ema(_swdoc.MF().fld_r_sla, true);
		}
	}
	else
	{

		_swdoc.ACTION().fk_ci_id = _swdoc.GetArg("fk_ci_id");
		TabCond.ShowTabItem(2,false);		
		_swdoc.ACTION().periodseconds = "0";

		_swdoc.LoadRecordData("swdata", "config_itemi", _swdoc.ACTION().fk_ci_id);
		_swdoc.UpdateFormFromData();
	}

	if(_swdoc.GetArg("service")=="1")
	{
		_evi(_swdoc.MF().ci_item, false);
		_evi(_swdoc.MF().service, true);
		_slf(_swdoc.MF().Field5 , "_swc_flg_sc=1&_swc_fk_config_type="+pfu(_swdoc.GetArg("ck_config_type")));
		_ete(_swdoc.MF().fld_fk_ci_item, 'Service');
		_ete(_swdoc.MF().fld_text_title, 'Set Service Item');
		_ewi(_swdoc.MF().fld_text_title , 100);
	}
	else
	{
		_slf(_swdoc.MF().Field5 , "_swc_fk_config_type="+pfu(_swdoc.GetArg("ck_config_type")));
	}
	//-- show diary update
	TabCond.ShowTabItem(3,_swdoc.ACTION().flg_diaryupdate=="1");
	_etab(TabCond, 0);

	//-- hide lb cmdb status
	_evi(lb_cmdbstatus, false);
	_evi(lb_ccis, false);
	_evi(tb_value, true);

	//-- set form modified to false as users complain about closing forms and getting prompted to save / cancel when they
	//-- havent done anything.
	_swdoc.ResetModiedFlag("ci_avail_baction");

	_een(btn_and1, (_swdoc.ACTION().execoncondition!=""));
	_een(btn_or1, (_swdoc.ACTION().execoncondition!=""));	
	_een(btn_apply_test1, (_swdoc.ACTION().execoncondition==""));

	_swdoc.setup_periodhhmm();

}

