btn_apply_test1_OnPressed()
{

	if((lb_op1.text=="")||(lb_test.text ==""))
	{
		MessageBox("Please specify some test criteria");
		return;
	}

	var boolString=false;
	var strOp = lb_op1.text;
	var strTest = "";
	switch(lb_test.text.toLowerCase())
	{
		case "breach type":
			strTest = "&[in_breachtype]";		
			boolString = true;
			break;
		case "breach value":
			strTest = "&[in_breachvalue]";		
			boolString = true;
			break;
		case "cmdb status":
			strTest = "&[in_current_cmdb_status]";		
			boolString = true;
			break;
		case "related configuration id":
			strTest = "&[in_relateditem_id]";		
			boolString = true;		
			break;			
		case "related cmdb status":
			strTest = "&[in_relateditem_cmdb_status]";		
			boolString = true;		
			break;			

	}

	//--
	//-- get appropriate value
	var strValue = tb_value.text;	
	if(lb_cmdbstatus.visible)strValue=lb_cmdbstatus.value;	
	if(lb_ccis.visible)strValue=lb_ccis.value;		

	if(boolString)
	{
		strTest = "'" + strTest + "'";
		strValue = "'" + strValue + "'";
	}

	var strFullTest = strTest + strOp + strValue;
	_swdoc.ACTION().execoncondition = _swdoc.ACTION().execoncondition + " " + strFullTest;
	_swdoc.UpdateFormFromData();

	_een(btn_apply_test1, false);
	_een(btn_and1, true);
	_een(btn_or1, true);

}

btn_and1_OnPressed()
{
	_swdoc.ACTION().execoncondition = _swdoc.ACTION().execoncondition + " " + " AND " + " ";
	_swdoc.UpdateFormFromData();

	_een(btn_apply_test1, true);
