}

btn_or1_OnPressed()
{

	_swdoc.ACTION().execoncondition = _swdoc.ACTION().execoncondition + " " + " OR " + " ";
	_swdoc.UpdateFormFromData();

	_een(btn_apply_test1, true);
	_een(btn_and1, false);
	_een(btn_or1, false);	

}

lb_test_OnValueChanged(strValue)
{
	//-- show inputs for user based on the test they are doing
	if(strValue.toLowerCase().indexOf("cmdb status")!=-1)
	{
		_evi(lb_cmdbstatus, true);
		_een(lb_cmdbstatus, true);
		_evi(lb_ccis, false);		
		_evi(tb_value, false);
	}
	else if(strValue.toLowerCase().indexOf("configuration id")!=-1)
	{
		_evi(lb_ccis, true);
		_evi(tb_value, false);
		_evi(lb_cmdbstatus, false);
	}
	else
	{
		_evi(tb_value, true);
		_evi(lb_cmdbstatus, false);
		_evi(lb_ccis, false);
	}

}

