//-- init form fields
function setup_form_fields(boolInsert)
{
	_swdoc.boolInsertingType = boolInsert;

	_een(_swdoc.MF().tb_code, boolInsert); //-- enable code change when inserting
	
	_een(_swdoc.MF().btn_new_status, (!boolInsert));
	_een(_swdoc.MF().btn_new_reltype, (!boolInsert));	
	_een(_swdoc.MF().btn_new_att, (!boolInsert));
	_een(_swdoc.MF().btn_new_status1, (!boolInsert));
	
	_swdoc.MF().tabType.ShowTabItem(1,!boolInsert);
	//-- Merope - FR6.1.3, remove service request definitions
	//MF().tabType.ShowTabItem(3,!boolInsert);
	//MF().tabother.ShowTabItem(3,_swdoc.oType().flg_comps==1);
	_swdoc.MF().tabother.ShowTabItem(3,!boolInsert);
	_etab(_swdoc.MF().tabType, 0);
	if(_swdoc.oType().pk_config_type.indexOf('->')<0)
	{
		_een(_swdoc.MF().lbl_fk_config_type, false);
		_een(_swdoc.MF().flg_inherit_reqtypes, false);
		_een(_swdoc.MF().flg_inherit_autodiary, false);
		_een(_swdoc.MF().flg_inherit_protected, false);
		_een(_swdoc.MF().flg_inherit_regex, false);
	}else
	{
		_een(_swdoc.MF().lbl_fk_config_type, true);
	}
	_swdoc.orig_cost = _swdoc.oType().bus_cost;
	_swdoc.orig_price = _swdoc.oType().bus_price;
	_etab(_swdoc.MF().tabother, 0);
		
	_swdoc.strConfigType = _swdoc.oType().pk_config_type;
	_swdoc.strDisplayType = _swdoc.oType().type_display;

}

						OnDataSaved()
