							btn_del_subtype_OnPressed()
{

	//--
	//-- Get delete type and make sure it doesnt have any associated items that are activated
	var delTypeID = sl_subtypes.GetItemTextRaw(sl_subtypes.curSel, 'pk_config_type');

	var arrParams ={};arrParams.ck_config_type=delTypeID;
	var intRowCount = app.g.sqs_rowcountbycol("config_itemi",arrParams);
	//-- are there any items associated?
	if(intRowCount>0)
	{
		MessageBox("There are configuration items in the CMDB that are still associated to this configuration type.\nThey need to be re-assigned using the configuration manager before you can delete this configuration type.")
		return false;
	}
	
	//--
	//-- if it has sub types cannot delete
	var arrParams ={};arrParams.fk_config_type=delTypeID;
	var intRowCount = app.g.sqs_rowcountbycol("config_typei",arrParams);
	if(intRowCount>0)
	{
		MessageBox("There are sub configuration types associated to this configuration type.\nThey need to be deleted before you can delete this one.")
		return false;
	}
	
	
	//-- delete
	var strConfirm = "Are you sure you want to delete this configuration type?";
	if(app.g.sl_deldbrow(sl_subtypes,sl_subtypes.curSel,"pk_config_type",null,strConfirm))
	{
		//-- delete related records
		app.g.delete_tablebycol("ct_statusl","fk_config_type",delTypeID);
		app.g.delete_tablebycol("ct_reltypes","fk_config_type",delTypeID);
		app.g.delete_tablebycol("ct_profiles","fk_config_type",delTypeID);		
	}

}

