							btn_del_status_OnPressed()
{
	var strConfirm = "Are you sure you want to delete the selected status level?";
//	app.g.sl_deldbrow(sl_status,sl_status.curSel,0,null,strConfirm);
	var delRow = sl_status.curSel;
	var keyCol = dd.tables[LC(sl_status.table)].PrimaryKey;
	
	var strTable = sl_status.table;
	var strKey   = sl_status.GetItemTextRaw(delRow,keyCol);
	var strStatus   = sl_status.GetItemTextRaw(delRow,"status_level");
	if (strKey == "") return false;

	if ((strConfirm!=undefined) && (strConfirm!= null) && (strConfirm!=""))
	{
		if (!confirm(strConfirm)) return false;
	}

	if (app.g.sqlexecute_delete(strTable,strKey))
	{
		if(confirm("Would you like to remove this status from sub categories in the cmdb?"))
		{
			var xmlmc = new XmlMethodCall;
			
			xmlmc.SetValue("status", strStatus);
			xmlmc.SetValue("type", config_typei.pk_config_type);
			xmlmc.SetValue("sc", 0);	
			
			//-- Invoke the method
			if(xmlmc.Invoke("VPME", "cmdbDeleteStatus"))
			{
				//_swdoc.CloseForm();
			}
			else
			{
				MessageBox(xmlmc.GetLastError());
			}	
		}
		sl_status.RemoveRow(delRow);
		sl_status.SetRowSelected(0);

		
	}

	return true;


}

							btn_del_reltype_OnPressed()
{
	var strConfirm = "Are you sure you want to delete the selected relationship type association?";
//	app.g.sl_deldbrow(sl_reltypes,sl_reltypes.curSel,'pk_ctrel_id',null,strConfirm);

	var delRow = sl_reltypes.curSel;
	var keyCol = dd.tables[LC(sl_reltypes.table)].PrimaryKey;
	
	var strTable = sl_reltypes.table;
	var strKey   = sl_reltypes.GetItemTextRaw(delRow,keyCol);
	var strRel  = sl_reltypes.GetItemTextRaw(delRow,"fk_reltype");
	if (strKey == "") return false;

	if ((strConfirm!=undefined) && (strConfirm!= null) && (strConfirm!=""))
	{
		if (!confirm(strConfirm)) return false;
	}

	if (app.g.sqlexecute_delete(strTable,strKey))
	{
		if(confirm("Would you like to remove this relationship from sub categories in the cmdb?"))
		{
			var xmlmc = new XmlMethodCall;
			
			xmlmc.SetValue("relationship", strRel);
			xmlmc.SetValue("type", config_typei.pk_config_type);
			xmlmc.SetValue("sc", 0);	
			
			//-- Invoke the method
			if(xmlmc.Invoke("VPME", "cmdbDeleteRels"))
			{
				//_swdoc.CloseForm();
			}
			else
			{
				MessageBox(xmlmc.GetLastError());
			}	
		}
		sl_reltypes.RemoveRow(delRow);
		sl_reltypes.SetRowSelected(0);

		
	}

	return true;



}

