							mbtn_rt_actions1_OnMenuItem(strName, nItemID)
{
	var iAssignType = 1;
	var iRemoveType = 2;	


	if(nItemID == iAssignType)
	{
		var strURL = "mode=select";
		app.OpenFormForAdd("browse_requesttypes", "", strURL, true, function(oDoc){
			if(oDoc.strSelectedKey!="")
			{
				//-- insert ct_profiles 
				var p = {};
				p.fk_config_type=_swdoc.oType().pk_config_type
				p.fk_typecode=oDoc.strSelectedKey;
				p.type_info=oDoc.strSelectedText;
				if(app.g.submittableinsert("ct_profiles",arrCols))
				{
					app.g.sl_refresh(sl_profiles,null,0);
					sl_profiles_OnItemSelected(0);
				}		
			}
			return;
		});
    }
	else if(nItemID == iRemoveType)
	{
		var strConfirm = "Are you sure you want to remove this service request association?";
		app.g.sl_deldbrow(sl_profiles,sl_profiles.curSel,'pk_auto_id',null,strConfirm);		
	}
	




}

