							tabother_OnTabSelected(nTab)
{
	// TODO: Add your event handler code here
	var tStats = 0;
	var tType = 1;
	var tForm = 2;
	var tConf = 3;
	if (nTab==tStats)
	{
		_swdoc.MF().sl_status.Refresh();
	}else if (nTab==tType)
	{
		_swdoc.MF().sl_reltypes.Refresh();
	}else if (nTab==tForm)
	{
		_swdoc.MF().sl_atts.Refresh();
	}else if (nTab==tConf)
	{
		_swdoc.MF().sl_itemdefs.Refresh();
	}

}

							tabType_OnTabSelected(nTab)
{
	// TODO: Add your event handler code here
	if(nTab==1)
	{
		_swdoc.MF().sl_subtypes.Refresh();
	}else if(nTab==2)
	{
		_swdoc.MF().sl_profiles.Refresh();
	}

}

