function reset_form_fields()
{
	//-- clear search fields
	app.g.clear_form_fields("config_itemi",mainform,"",false);

	_ete(_swdoc.MF().lb_abln, "Yes");

	//-- reset date values of ci other and ext fields
	_swdoc.ext_field1_fromdate = 0;_swdoc.ext_field1_todate = 0;
	_swdoc.ext_field2_fromdate = 0;_swdoc.ext_field2_todate = 0;
	_swdoc.ext_field3_fromdate = 0;_swdoc.ext_field3_todate = 0;
	_swdoc.ext_field4_fromdate = 0;_swdoc.ext_field4_todate = 0;

	//-- used to store date values if other ci fields are being used for dates
	_swdoc.ci_field1_fromdate = 0;_swdoc.ci_field1_todate = 0;
	_swdoc.ci_field2_fromdate = 0;_swdoc.ci_field2_todate = 0;
	_swdoc.ci_field3_fromdate = 0;_swdoc.ci_field3_todate = 0;
	_swdoc.ci_field4_fromdate = 0;_swdoc.ci_field4_todate = 0;	


	_swdoc.load_other_fields();
	
	//-- disable ext search fields
	_swdoc.enable_ext_fields(false);
}

function load_other_fields()
{
	//-- setup other ci fields
	var arrIncludeOnlyFields = new Array();
	var oRS = app.g.get_sqrecordset("select.table","table=cmdb_srch_fields");
	while(oRS.Fetch())
	{
		var strField = app.g.get_field(oRS,"pk_fieldname");
		arrIncludeOnlyFields[strField] = true;
	}
	
	var strList = app.g.dd_getcolumnlist("config_itemi",true, arrIncludeOnlyFields);
	strList = "Select Field...^0|" + strList;
	_swdoc.MF().ci_field1.pickList = strList;
	_swdoc.MF().ci_field2.pickList = strList;
	_swdoc.MF().ci_field3.pickList = strList;
	_swdoc.MF().ci_field4.pickList = strList;
	_eva(_swdoc.MF().ci_field1_value, "");
	_eva(_swdoc.MF().ci_field2_value, "");
	_eva(_swdoc.MF().ci_field3_value, "");
	_eva(_swdoc.MF().ci_field4_value, "");
}

function enable_ext_fields(boolEnable, strType)
{
	
	if(boolEnable)
	{
		boolEnable = false;
	
		//-- load type table name if it has one and get column names from dd object
		var p={};p.pk_config_type = strType
		var oRS = app.g.get_tablerecordset_bycol("config_typei",p,false,"extended_table");	
		if(oRS.Fetch())
		{
			var strExtTable = app.g.get_field(oRS,"extended_table");
			if((strExtTable!="") && (dd.tables[strExtTable]))
			{
				//-- get column list
				var strList = app.g.dd_getcolumnlist(strExtTable,true);
				strList = "Select Field...^0|" + strList;
				_swdoc.MF().ext_field1.pickList = strList;
				_swdoc.MF().ext_field2.pickList = strList;
				_swdoc.MF().ext_field3.pickList = strList;
				_swdoc.MF().ext_field4.pickList = strList;
				boolEnable = true;
				
				_swdoc.strTypeTable = strExtTable;
			}
		}
	}
	
	//-- col fields
	_een(_swdoc.MF().ext_field1, boolEnable);
	_een(_swdoc.MF().ext_field2, boolEnable);	
	_een(_swdoc.MF().ext_field3, boolEnable);	
	_een(_swdoc.MF().ext_field4, boolEnable);
	_eva(_swdoc.MF().ext_field1, "0");
	_eva(_swdoc.MF().ext_field2, "0");
	_eva(_swdoc.MF().ext_field3, "0");
	_eva(_swdoc.MF().ext_field4, "0");


	//-- value fields
	_een(_swdoc.MF().ext_field1_value, boolEnable);
	_een(_swdoc.MF().ext_field2_value, boolEnable);
	_een(_swdoc.MF().ext_field3_value, boolEnable);
	_een(_swdoc.MF().ext_field4_value, boolEnable);
	_eva(_swdoc.MF().ext_field1_value, "");
	_eva(_swdoc.MF().ext_field2_value, "");
	_eva(_swdoc.MF().ext_field3_value, "");
	_eva(_swdoc.MF().ext_field4_value, "");
	
}

function filter_avail_list()
{
	var strMonitorFilter = "sf=cilist_monitored";
	if(_swdoc.MF().tb_mgroup_filter.text!="") 
	{
		strMonitorFilter = "sf=cilist_monitored_filtered&mg="+_swdoc.MF().tb_mgroup_filter.text;
	}
	
	_slf(_swdoc.MF().sl_availitems , strMonitorFilter);

}

							btn_search_OnPressed()
{
	//-- 19.01.2006 - NWJ
	//-- create search criteria
	
	_swdoc.strFilter = ""; 
	var intPageNo = 1;
	_swdoc.intPageNo = 1;
	
	_etab(TabRes, 0);
	_een(btn_del_ci, false);
	_een(mbtn_view_ci, false);
	
	app.g.sqllist_clear(sl_results);

	var boolAND = (lb_type.value==1);
	var strFilter = app.g.create_search_params(mainform,"config_itemi","",boolAND,false);

	//-- get other ci fields	
	if(ci_field1_value.value!="")
	{
		var intColPos = ci_field1.value-1;
		if(intColPos>-1)
		{
			var strColName = dd.tables['config_itemi'].columns[intColPos].Name;
			
			if(_swdoc.ci_field1_fromdate>0)
			{
				//-- using date
				strFilter += "&_swcdate_" + strColName + "="+ _swdoc.ci_field1_fromdate +"," + _swdoc.ci_field1_todate;	
			}
			else
			{
				strFilter += "&_swc_" + strColName + "="+ ci_field1_value.value;
			}
		}
	}
	if(ci_field2_value.value!="")
	{
		var intColPos = ci_field2.value-1;
		if(intColPos>-1)
		{
			var strColName = dd.tables['config_itemi'].columns[intColPos].Name;
			
			if(_swdoc.ci_field2_fromdate>0)
			{
				//-- using date
				strFilter += "&_swcdate_" + strColName + "="+ _swdoc.ci_field2_fromdate +"," + _swdoc.ci_field2_todate;		
			}
			else
			{
				strFilter += "&_swc_" + strColName + "="+ ci_field2_value.value;	
			}
		}
	}
	if(ci_field3_value.value!="")
	{
		var intColPos = ci_field3.value-1;
		if(intColPos>-1)
		{
			var strColName = dd.tables['config_itemi'].columns[intColPos].Name;
		
			if(_swdoc.ci_field3_fromdate>0)
			{
				//-- using date
				strFilter += "&_swcdate_" + strColName + "="+ _swdoc.ci_field3_fromdate +"," + _swdoc.ci_field3_todate;		
			}
			else
			{
				strFilter += "&_swc_" + strColName + "="+ ci_field2_value.value;		
			}
		}
	}
	if(ci_field4_value.value!="")
	{
		var intColPos = ci_field4.value-1;
		if(intColPos>-1)
		{
			var strColName = dd.tables['config_itemi'].columns[intColPos].Name;
			
			if(_swdoc.ci_field4_fromdate>0)
			{
				//-- using date
				strFilter += "&_swcdate_" + strColName + "="+ _swdoc.ci_field4_fromdate +"," + _swdoc.ci_field4_todate;		
			}
			else
			{
				strFilter += "&_swc_" + strColName + "="+ ci_field2_value.value;		
			}
		}
	}			
	
	//-- get extended details criteria
	if(ext_field1.enable)
	{
		var strExendedSearchCriteria = "";
	 
		var intColPos = 0;
		var strColName = "";
		var strQuote = "";		
		//-- search on ext field 1
		if((ext_field1.value>0) &&(ext_field1_value.value!=""))
		{
			intColPos  = ext_field1.value-1;
			strColName = dd.tables[_swdoc.strTypeTable].columns[intColPos].Name;
			
			if(_swdoc.ext_field1_fromdate>0)
			{
				//-- using date
				strFilter += "&_swcextdate_" + strColName + "="+ _swdoc.ext_field1_fromdate +","+_swdoc.ext_field1_todate;				
			}
			else
			{
				strFilter += "&_swcext_" + strColName + "="+ ext_field1_value.value;			
			}
		}

		//-- search on ext field 2
		if((ext_field2.value>0) &&(ext_field2_value.value!=""))
		{
			intColPos  = ext_field2.value-1;
			strColName = dd.tables[_swdoc.strTypeTable].columns[intColPos].Name;
			
			if(_swdoc.ext_field2_fromdate>0)
			{
				//-- using date
				strFilter += "&_swcextdate_" + strColName + "="+ _swdoc.ext_field2_fromdate +","+_swdoc.ext_field2_todate;					
			}
			else
			{		
				strFilter += "&_swcext_" + strColName + "="+ ext_field2_value.value;				
			}
		}

		//-- search on ext field 3
		if((ext_field3.value>0) &&(ext_field3_value.value!=""))
		{
			intColPos  = ext_field3.value-1;
			strColName = dd.tables[_swdoc.strTypeTable].columns[intColPos].Name;
			
			if(_swdoc.ext_field3_fromdate>0)
			{
				//-- using date
				strFilter += "&_swcextdate_" + strColName + "="+ _swdoc.ext_field3_fromdate +","+_swdoc.ext_field3_todate;									
			}
			else
			{		
				strFilter += "&_swcext_" + strColName + "="+ ext_field3_value.value;					
			}
		}
		
		//-- search on ext field 4
		if((ext_field4.value>0) &&(ext_field4_value.value!=""))
		{
			intColPos  = ext_field4.value-1;
			strColName = dd.tables[_swdoc.strTypeTable].columns[intColPos].Name;
			
			if(_swdoc.ext_field4_fromdate>0)
			{
				//-- using date
				strFilter += "&_swcextdate_" + strColName + "="+ _swdoc.ext_field4_fromdate +","+_swdoc.ext_field4_todate;													
			}
