	var strSelKeys = app.g.sl_getselected_valuesdel(sl_results	,0,",","");
	var strSubKeys = app.g.sl_get_valuesdel(sl_sub,0,",","");
	
	//-- if we already have sub calls
	if(strSubKeys!="")
	{
		strSelKeys += "," + strSubKeys;
	}
	
	var strFilter = "pids=" + strSelKeys;
	app.g.sl_filter(sl_sub,strFilter,null,0);

}

							OnFormLoaded()
{
	//-- set field states
	_swdoc.currentList = mainform.sl_results;
	_een(btn_subset, false);
	_een(btn_remsub, false);
	_een(btn_subclear, false);
	_een(mbtn_view_ci, false);
	_een(btn_common_update, false);

	//-- check if we are allowed to delete cis
	var intCanDelete = dd.GetGlobalParamAsString("Application Settings/CMDB/AllowDatabaseDelete");	
	_evi(btn_del_ci, (intCanDelete==1));
	_een(btn_del_ci, false);
	//-- if cmdb manager show manage fields
	_evi(btn_mng_fields, app.cmdb.can_manage(false));
	
	//-- default
	mainform.SetTimer(1, 5);
}

