							TabRes_OnTabSelected(nTab)
{
	if(nTab==0)
	{
		_swdoc.currentList = sl_results;
	}
	else if (nTab==1)
	{
		_swdoc.currentList = sl_sub;
		sl_sub.Refresh();
	}
	else if (nTab==2)
	{
		_swdoc.filter_avail_list();
		
		_swdoc.currentList = sl_availitems;
		sl_availitems.Refresh();
		_evi(btn_del_ci, false);
		_evi(btn_add_ci, false);
		_evi(btn_common_update, false);
		return;
	}
	
	_evi(btn_del_ci, true);
	_evi(btn_add_ci, true);		
	_evi(btn_common_update, true);
	
	_een(mbtn_view_ci, (app.g.sl_getselected_count(_swdoc.currentList)==1));
	_een(btn_del_ci, (_swdoc.currentList.rowCount()>0));			
	_een(btn_common_update, (app.g.sl_getselected_count(_swdoc.currentList)>1));		
	
}

							btn_add_ci_OnPressed()
{
	app.cmdb.create_configitem("",true);
}

							btn_del_ci_OnPressed()
{
	var strDeleteItems = app.g.sl_getselected_valuesdel(_swdoc.currentList,0,",");
	var strConfirm = "Are you sure that you want to delete the selected items from the database?"
	if(confirm(strConfirm))
	{
		var boolPromptForBaselineDelete = (dd.GetGlobalParamAsString("Application Settings/CMDB/OnItemDeleteConfirmBaselineDelete")=="1");	
		var boolDeleteBaselines = (dd.GetGlobalParamAsString("Application Settings/CMDB/OnItemDeleteDeleteBaselines")=="1")
		if(boolPromptForBaselineDelete)
		{
			boolDeleteBaselines = confirm("Would you like to delete all previous baselines for the selected items?");
		}
		
		if(app.cmdb.delete_configitem(strDeleteItems,boolDeleteBaselines))
		{
			//-- remove deleted items
			app.g.sl_clearrows(sl_results,strDeleteItems,0,null,0);
			app.g.sl_clearrows(sl_sub,strDeleteItems,0,null,0);
			sl_results_OnItemSelected(0);
			sl_sub_OnItemSelected(0);
		}
	}

}

							mbtn_view_ci_OnMenuItem(strName, nItemID)
{
  	var iProperties = 1;
	var iVCMParents = 2;	
	var iVCMChildren = 5;		
	var iInventory = 3;
	var iChangeSchedule = 4;

	//-- get intCI
  	var intCI = app.g.sl_getrawcolvalue(_swdoc.currentList,-1,"pk_auto_id");
  	if(intCI>0)
  	{
  		//-- pass in text identfier as well
  		var strItem = app.g.sl_getrawcolvalue(_swdoc.currentList,-1,"ck_config_item");
  	
		if(nItemID == iProperties)
		{
			app.cmdb.popup_configitem(intCI,true,"",false,function()
			{
				return;
			});
    	}
		else if(nItemID == iVCMParents)
		{
			app.cmdb.view_vcm(intCI,true);
		}
		else if(nItemID == iVCMChildren)
		{
			app.cmdb.view_vcm(intCI,false);
		}
		else if(nItemID == iInventory)
		{
			app.cmdb.view_inventory(intCI,strItem);
		}
		else if(nItemID == iChangeSchedule)
		{
			app.cmdb.view_schedule(intCI,strItem);
		}
	}
}

