							btn_common_update_OnPressed()
{
	//-- get items we want to update
	var strCIkeys = app.g.sl_getselected_valuesdel(_swdoc.currentList,0,",");
	
	//--
	//-- get each value from the sqllist and construct URL
	var strURL = "";
	var arrColsChecked = new Array();
	for (var y=1; y < _swdoc.currentList.colCount;y++)
	{
		//-- get details
		var strColName	= _swdoc.currentList.GetColumnName(y);
		var strCheckValue = app.g.sl_common_selcolvalue(_swdoc.currentList,strColName);
		//-- construct url params
		if(strURL!="")strURL +="&";
		
		if(arrColsChecked[strColName]==undefined)
		{
			arrColsChecked[strColName]=true;
			strURL += _swdoc.currentList.table + "." + strColName + "=" + strCheckValue;
		}
	}

	
	app.cmdb.common_ci_update(strCIkeys,strURL);
	//-- any code after this will have to be added as callback paramenter to app.cmdb.common_ci_update

}

