							ext_field1_value_OnLeftClick()
{
	//-- check if meant to be a date field if so popup date selector
 	//-- get control type (we are interested in dates)
	if(ext_field1.value==0)return true; 	
 	var intControlType = app.g.dd_fieldtype(_swdoc.strTypeTable,ext_field1.value-1);
 	if(intControlType==5)
 	{
 		//-- popup date selector and put value into 
 		
 		var strFieldLabel = app.g.dd_fieldlabel(_swdoc.strTypeTable,ext_field1.value-1);
 		app.g.popup("popup_date_selector","label_name=" + strFieldLabel, function(aForm)
 		{
 			if (aForm)
 			{
 				if(aForm.document.fromDate!="")
 				{
 					var arrFrom = aForm.document.fromDate.split("|");
					var arrTo = aForm.document.toDate.split("|"); 			
					_eva(ext_field1_value, "Between " + arrFrom[1].substring(0,10) + " and " + arrTo[1].substring(0,10));
 					
 					_swdoc.ext_field1_fromdate = arrFrom[0];
 					_swdoc.ext_field1_todate = arrTo[0];
 				}
 				return false;
 			}
 		});
	}

	_swdoc.ext_field1_fromdate = 0;
	_swdoc.ext_field1_todate = 0;
	return true;
}

