method.code:
						function sType()
{
	return _swdoc.ssrequesttype;
}

function MF()
{
	return _swdoc.mainform;
}

							OnFormLoaded()
{

	if(_swdoc.sType().pk_code!="")
	{
		var oTmp = app.g.get_group_detail(_swdoc.sType().assignto_group);//app.g.get_analystorgroup(_swdoc.sType().assignto_group,_swdoc.sType().assignto_owner);
		if(oTmp)
		{
			_ete(tb_suppgroup,oTmp.name);						
			//tb_suppowner.text=oTmp.oAnalyst.name;
		}
		
	}
	
	if(lb_callclass.text=="Change Request")
	{
		_ema(lb_process,true);
	}
	else
	{
		_ema(lb_process,false);	
	}	

	if(lb_type.text=="1")
	{
		_ema(lb_script,true);	
		_een(lb_script,true);		
	}
	else
	{
		_ete(lb_script,"");
		_een(lb_script,false);
		_ema(lb_script,false);
	}	

	
	//-- reset modied flag
	_swdoc.ResetModiedFlag("ssrequesttype");

}

							lb_profile1_OnLeftClick()
{
	// TODO: Add your event handler code here


	return false;
}

							tb_suppowner_OnDropDown()
{
	//-- pick analyst or group
	app.g.pick_analystorgroup("Assign To", function(oTmp)
	{
		if((oTmp)&&(oTmp.oGroup!=false))
		{
			//-- we have info
			_swdoc.sType().assignto_group = oTmp.oGroup.id;
			_swdoc.sType().assignto_owner = oTmp.oAnalyst.id;		
			_ete(tb_suppgroup,oTmp.oGroup.name);						
			//tb_suppowner.text=oTmp.oAnalyst.name;
			_swdoc.UpdateFormFromData();
		}
	});
	
	return false;
}
