						OnSaveData()
{

	if(_swdoc.boolInserting)
	{
		if(remote_support.url!="") {
			var p = {};
			p.url = remote_support.url;
			var intCount = app.g.staticfilter_rowcount("remote_support", "remote_support.by_url", p);
			if(intCount>0) {
				MessageBox('A Remote Support configuration already exists with this url.');
				return false;
			}
		}	
	}

	if(_swdoc.remote_support.api_password != mainform.tbRetypePassword.text)
	{
		MessageBox("Please check that your password and confirmation match.", MB_ICONEXCLAMATION | MB_OK);
		return false;
	}

	return true;
}

