							OnFormLoaded(bDataReloaded)
{
	// TODO: Add your event handler code here
	_swdoc.boolInserting = remote_support.name=="";
	//-- call php to get mailbox list
	var strURL = "php/itsm/get_setting_options.php?option=remotetools"; 
	var strResult = app.g.submit_php(strURL,true);
	mainform.fld_type.pickList = strResult;
	
	//--Enable use of this code to show list of all fields from CI form
	//app.g.dd_getcolumnlist("config_itemi",false, "", mainform.lb_lookup , true, true);
		
	if(_swdoc.remote_support.flg_se_enabled==1)
	{
		_een(grpDiarySettings , false);
	}
	else
	{
		_een(grpDiarySettings , true);
	}
	
	if(_swdoc.remote_support.api_password!="")
	{
		_ete(mainform.tbRetypePassword , _swdoc.remote_support.api_password);
	}
	
	if(_swdoc.boolInserting)
	{
		tRemote.ShowTabItem(2,false); 	
		tRemote.ShowTabItem(1,false); 	
	}

}

							sl_rc1_OnItemSelected(nRow)
{
	// TODO: Add your event handler code here
	var rc_id = app.g.sl_getrawcolvalue(sl_rc1, sl_rc1.curSel, "pk_auto_id");
	if (rc_id == "")
	{
		_een(mbtn_rc_view1, false);
	}
	else
	{
		_een(mbtn_rc_view1, true);
	}

}

							mbtn_rc_view1_OnMenuItem(strName, nItemID)
{

	var iProperties = 1;	
  	if(nItemID == iProperties)
	{
		var strKeyValue = sl_rc1.GetItemTextRaw(sl_rc1.curSel, app.g.dd_primarykey(sl_rc1.table));
		app.OpenFormForEdit("company_rc_tools", strKeyValue, "", true,function(){});
	}

}

							mbtn_rc_actions1_OnMenuItem(strName, nItemID)
{
	var iAddSite = 1;
	var iRemoveSite = 2;
	
	//-- Add Site
	if(nItemID == iAddSite)
	{
		var strURL = "fk_tool=" + pfu(remote_support.name);
		app.OpenFormForAdd("company_rc_tools", "", strURL, true, function(){
			MessageBox("HIIIaaa");
			_swdoc.MF().sl_rc1.Refresh();
		});
	}
	else if(nItemID == iRemoveSite)
	{
		//-- 13.04.2005
		//-- NWJ
		//-- delete selected site (if it has contacts update them)
    	var selectedRow     = sl_rc1.curSel;
    	var strConfirm		= "Are you sure you want to delete this Remote Support Tool from the Organisation?";
		var strDeptKey      = sl_rc1.GetItemTextRaw(selectedRow,"pk_id");    
    	
		if (app.g.sl_deldbrow(sl_rc1,sl_rc1.curSel,'pk_id',null,strConfirm))
		{
			//-- remove link to company contacts
			//-- done in vpme
			if (app.g.sl_getselected_count(sl_rc1)>0)
			{
				_een(mbtn_rc_view1, true);
			}
			else
			{
				_een(mbtn_rc_view1, false);
			}
			_swdoc.MF().sl_rc1.Refresh();
		}
	}
}

