							mbtn_rc_actions2_OnMenuItem(strName, nItemID)
{
	var iAddSite = 1;
	var iRemoveSite = 2;
	
	//-- Add Site
	if(nItemID == iAddSite)
	{
		var strURL = "fk_tool=" + pfu(remote_support.name);
		app.OpenFormForAdd("support_logins", "", strURL, true, function(){
			_swdoc.MF().sl_login.Refresh();
		});
	}
	else if(nItemID == iRemoveSite)
	{
		//-- 13.04.2005
		//-- NWJ
		//-- delete selected site (if it has contacts update them)
    	var selectedRow     = sl_login.curSel;
    	var strConfirm		= "Are you sure you want to delete this Remote Support Login from the Remote Support Tool?";
		var strDeptKey      = sl_login.GetItemTextRaw(selectedRow,"pk_auto_id");    
    	
		if (app.g.sl_deldbrow(sl_login,sl_login.curSel,'pk_auto_id',null,strConfirm))
		{
			//-- remove link to company contacts
			//-- done in vpme
			if (app.g.sl_getselected_count(sl_login)>0)
			{
				_een(mbtn_rc_view2, true);
			}
			else
			{
				_een(mbtn_rc_view2, false);
			}
			_swdoc.MF().sl_login.Refresh();
		}
	}
}

							fld_template_OnLeftClick()
{
	//-- get template list
	
	if(fld_template.pickList=="")
	{
		//-- call php to get mailbox list
		//MessageBox(lb_mailbox1.text)
		var strURL = "php/itsm/get_templatelist.php?mailboxname=" + fld_mailbox.text; 
		var strResult = app.g.submit_php(strURL,true);
		fld_template.pickList = strResult;
	}

	return true;
}

