							fld_rs_name_OnValueChanged(strValue)
{
	// TODO: Add your event handler code here

	var testString = new String(strValue);
	var re = new RegExp('^[a-zA-Z0-9]*$');
  	if (testString.match(re)) 
  	{
 		_swdoc.strCurrentName = strValue;
   	}
  	else 
  	{
  		_swdoc.remote_support["name"] = _swdoc.strCurrentName;
  		_swdoc.UpdateFormFromData();
    	MessageBox("The name of the configuration can only contain alphanumeric characters.");
  	}
}

